INSERT INTO `du_hoc_detail` VALUES ('4', '4', 'Nguyễn Hồng Phương Dung', '<p>\r\n	<img alt=\"\" src=\"/static/images/image/image013.jpg\" style=\"width: 188px; height: 177px;\" /><img alt=\"\" src=\"/static/images/image/image014.jpg\" style=\"width: 450px; height: 237px;\" /></p>\r\n<p>\r\n	Student: Nguyễn Hồng Phương Dung</p>\r\n<p>\r\n	Date of birth: 26/01/1992</p>\r\n<p>\r\n	Status: Got Visa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>\r\n	College/University: Foothill College</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	California, 25 th&aacute;ng 2, 2011</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Th&acirc;n gửi trung t&acirc;m Eduvisa,</p>\r\n<p>\r\n	Em l&agrave; Phương Dung, 20 tuổi. Hiện em đang l&agrave; du học sinh tại USA. Tuy c&oacute; được&nbsp; I &ndash; 20 của Foothill từ rất sớm nhưng em chưa d&aacute;m đi phỏng vấn v&igrave; kh&ocirc;ng biết phải chuẩn bị những g&igrave; cả về giấy tờ lẫn tinh thần. May sao nhờ c&oacute; Eduvisa đ&atilde; gi&uacute;p em rất nhiều m&agrave; em c&oacute; thể chuẩn bị đầy đủ sẵn s&agrave;ng để đi phỏng vấn lần đầu ti&ecirc;n v&agrave;o ng&agrave;y 10/2/2010. Thật ra l&uacute;c v&agrave;o l&atilde;nh sự qu&aacute;n Mỹ, em run lắm !!! Nhưng m&agrave; sau 2 tiếng rưỡi ngồi đợi th&igrave; em mừng lắm khi đến lượt m&igrave;nh. Em rất biết ơn anh chị khi phỏng vấn th&agrave;nh c&ocirc;ng</p>\r\n<p>\r\n	Mong đ&acirc;y l&agrave; tin vui cho cả gia đ&igrave;nh em v&agrave; anh chị ng&agrave;y đầu năm</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Th&acirc;n mến,</p>\r\n<p>\r\n	Phương Dung</p>', null);
