INSERT INTO `du_hoc_detail` VALUES ('14', '5', 'Thông báo về thời hạn của Visa Mỹ', '<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"/static/images/image/image001(1).jpg\" style=\"width: 100px; height: 100px;\" /></p>\r\n<p>\r\n	Trong tương lai, Hoa Kỳ c&oacute; thể sẽ chỉ cấp Visa c&oacute; thời hạn 3 th&aacute;ng cho c&ocirc;ng d&acirc;n Việt Nam muốn nhập cảnh Hoa Kỳ, v&igrave; phần lớn c&ocirc;ng d&acirc;n Hoa Kỳ đến Việt Nam cũng chỉ nhận được Visa c&oacute; thời hạn như vậy.</p>\r\n<p>\r\n	Thời hạn của visa kh&ocirc;ng định cư Hoa Kỳ được dựa tr&ecirc;n nguy&ecirc;n tắc c&oacute; đi c&oacute; lại (tương đương thời hạn m&agrave; nước kh&aacute;c cấp cho c&ocirc;ng d&acirc;n Hoa Kỳ khi họ xin loại Visa tương tự). Hiện tại, ch&uacute;ng t&ocirc;i cấp cho c&ocirc;ng d&acirc;n Việt Nam visa 12 th&aacute;ng.</p>\r\n<p>\r\n	Ch&uacute;ng t&ocirc;i tiếp tục hy vọng rằng ch&iacute;nh phủ Việt Nam sẽ cấp cho c&ocirc;ng d&acirc;n Hoa Kỳ visa c&oacute; thời hạn 12 th&aacute;ng, để tr&aacute;nh phải giảm thời hạn visa của c&ocirc;ng d&acirc;n Việt Nam, v&igrave; điều n&agrave;y sẽ ảnh hưởng ti&ecirc;u cực đến rất nhiều người xin visa.</p>\r\n<p>\r\n	Ph&aacute;i đo&agrave;n ngoại giao Hoa Kỳ tại Việt Nam hy vọng th&ocirc;ng tin n&agrave;y c&oacute; &iacute;ch cho đương đơn xin visa kh&ocirc;ng định cư khi l&ecirc;n kế hoạch tới Hoa Kỳ trong tương lai.</p>', null);
INSERT INTO `du_hoc_detail` VALUES ('15', '4', 'La Chí Hào', '<p>\r\n	<img alt=\"\" src=\"/static/images/image/image021.jpg\" style=\"width: 357px; height: 232px;\" /></p>\r\n<p>\r\n	Student: La Ch&iacute; H&agrave;o</p>\r\n<p>\r\n	Date of birth: 14/08/1989</p>\r\n<p>\r\n	Status : Got Visa</p>\r\n<p>\r\n	College/University: City College Of San Francisco</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p align=\"center\">\r\n	______________________</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Tp. HCM, 26 th&aacute;ng 6, 2010</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Em t&ecirc;n l&agrave; La Ch&iacute; H&agrave;o, hiện đang l&agrave; sinh vi&ecirc;n. V&agrave;o th&aacute;ng 1 vừa rồi em đ&atilde; đến trung t&acirc;m Eduvisa để nhờ tư vấn du học Mỹ v&agrave;o kh&oacute;a th&aacute;ng 3/2010 c&ugrave;ng với sự tư vấn của anh chị, em đ&atilde; c&oacute; những lời khuy&ecirc;n qu&yacute; gi&aacute; v&agrave; ch&acirc;n th&agrave;nh. C&aacute;c anh chị đ&atilde; gi&uacute;p em rất nhiều trong việc chuẩn bị hồ sơ sao cho tốt nhất c&ugrave;ng với những c&acirc;u hỏi thường gặp trong phỏng vấn xin visa, kết quả l&agrave; em đ&atilde; xin được visa Mỹ ngay trong lần đầu ti&ecirc;n</p>\r\n<p>\r\n	Giờ đ&acirc;y tương lai em đ&atilde; rộng mở hơn khi được đến Mỹ, tiếp thu những kiến thức mới v&agrave; học tập trong một m&ocirc;i trường c&oacute; chất lượng gi&aacute;o dục thuộc h&agrave;ng đầu thế giới v&agrave; ngay giờ đ&acirc;y, em đang chuẩn bị cho cuộc h&agrave;nh tr&igrave;nh đến nước Mỹ để thực hiện ước mơ của m&igrave;nh</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	La Ch&iacute; H&agrave;o</p>', null);
INSERT INTO `du_hoc_detail` VALUES ('16', '4', 'Lâm Tú Vinh', '<p>\r\n	<img alt=\"\" src=\"/static/images/image/image023.jpg\" style=\"width: 186px; height: 188px;\" /></p>\r\n<p>\r\n	Student: L&acirc;m T&uacute; Vinh</p>\r\n<p>\r\n	Date of birth: 07/02/1991</p>\r\n<p>\r\n	Status: Got Visa</p>\r\n<p>\r\n	College/University: De Anza College</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p align=\"center\">\r\n	______________________</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Califonia, ng&agrave;y 8 th&aacute;ng 12 năm 2009</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Em viết thư n&agrave;y l&agrave; để cảm ơn trung t&acirc;m EduVisa đ&atilde; hết l&ograve;ng gi&uacute;p đỡ em trong những ng&agrave;y chuẩn bị l&agrave;m thủ tục đi du học. Từ việc soạn c&acirc;u hỏi phỏng vấn đến việc tập trả lời, ngay cả đến khi n&agrave;y chuẩn bị phỏng vấn, em đ&atilde; nhận được rất nhiều lời động vi&ecirc;n, cổ vũ v&agrave; điều đ&oacute; đ&atilde; kh&iacute;ch lệ em rất nhiều. Nếu n&oacute;i một tiếng cảm ơn th&ocirc;i th&igrave; kh&ocirc;ng đủ đ&acirc;u ! Ch&iacute;nh v&igrave; thế, em đ&atilde; tự hứa l&agrave; sẽ cố gắng thật nhiều trong việc học sắp tới cũng như tương lai của em để c&oacute; thể đ&aacute;p lại tấm l&ograve;ng của anh chị. Kh&ocirc;ng những thế, em mong muốn rằng những lời n&oacute;i của m&igrave;nh c&oacute; thể trở th&agrave;nh một động lực lớn cho anh chị để trung t&acirc;m c&oacute; thể tiếp tục ph&aacute;t triển v&agrave; c&oacute; thể gi&uacute;p đỡ được th&ecirc;m cho thật nhiều bạn sinh vi&ecirc;n, học sinh muốn đi du học nước ngo&agrave;i để mai sau c&oacute; thể gi&uacute;p &iacute;ch cho đất nước. Để c&oacute; được buổi phỏng vấn th&agrave;nh c&ocirc;ng ng&agrave;y h&ocirc;m nay đ&oacute; l&agrave; một nổ lực kh&ocirc;ng nhỏ của anh chị d&agrave;nh cho em.</p>\r\n<p>\r\n	Em xin ch&acirc;n th&agrave;nh cảm ơn trung t&acirc;m EduVisa!</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	T&uacute; Vinh</p>', null);
INSERT INTO `du_hoc_detail` VALUES ('17', '4', 'Trần Thị Mười', '<p>\r\n	<img alt=\"\" src=\"/static/images/image/image025.jpg\" style=\"width: 312px; height: 189px;\" /></p>\r\n<p>\r\n	Trần Thị Mười</p>\r\n<p>\r\n	Trưởng ph&ograve;ng kinh doanh cao cấp tại Manulife</p>\r\n<p align=\"center\">\r\n	______________________</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Thư c&aacute;m ơn !</p>\r\n<p>\r\n	Cuộc sống lu&ocirc;n c&oacute; những điều thật kỳ diệu xảy ra !</p>\r\n<p>\r\n	Ng&agrave;y 10/05/2010, t&ocirc;i v&agrave; chồng t&ocirc;i l&agrave; anh V&otilde; Duy Kh&aacute;i( giảng vi&ecirc;n trường cao đẳng T&agrave;i Ch&iacute;nh &ndash; Hải Quan ), sẽ l&ecirc;n đường sang Mỹ để dự lễ tốt nghiệp đại học của con g&aacute;i ch&uacute;ng t&ocirc;i l&agrave; V&otilde; Duy Đan Hạ</p>\r\n<p>\r\n	Sự nhiệt t&igrave;nh v&agrave; c&aacute;ch l&agrave;m việc chuy&ecirc;n nghiệp của anh em trung t&acirc;m du học Mỹ EduVisa kh&ocirc;ng những đ&atilde; gi&uacute;p cho con t&ocirc;i, gia đ&igrave;nh t&ocirc;i m&agrave; c&ograve;n gi&uacute;p cho c&aacute;c ch&aacute;u t&ocirc;i: Phan Th&aacute;i Ng&acirc;n, Ho&agrave;ng Phạm Tr&acirc;m Anh, Ho&agrave;ng Phạm Tr&iacute; Anh&hellip; đ&atilde; v&agrave; đang học tại Houston, Texas.</p>\r\n<p>\r\n	T&ocirc;i xin ch&acirc;n th&agrave;nh cảm ơn ban gi&aacute;m đốc của EduVisa . Ch&uacute;c sức&nbsp; khỏe v&agrave; c&ocirc;ng việc ng&agrave;y c&agrave;ng ph&aacute;t triển để gi&uacute;p c&aacute;c ch&aacute;u đạt được ước mơ của m&igrave;nh, bay xa hơn v&agrave; vươn tới những tầm cao mới của khoa học, của Thế Giới !</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tr&acirc;n trọng k&iacute;nh ch&agrave;o !</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trần Thị Mười</p>', null);
INSERT INTO `du_hoc_detail` VALUES ('18', '4', 'Vũ Vạn Kim', '<p>\r\n	<img alt=\"\" src=\"/static/images/image/image029.jpg\" style=\"width: 186px; height: 182px;\" /><img alt=\"\" src=\"/static/images/image/image028.jpg\" style=\"width: 328px; height: 204px;\" /></p>\r\n<p>\r\n	Student: Vũ Vạn Kim</p>\r\n<p>\r\n	Date of birth: 10/02/1991</p>\r\n<p>\r\n	Status : Got Visa</p>\r\n<p>\r\n	College/University: Houston Community College</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p align=\"center\">\r\n	______________________</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Gửi lời cảm ơn đến EduVisa</p>\r\n<p>\r\n	Những n&eacute;t b&uacute;t đầu ti&ecirc;n, xin gửi lời cảm ơn v&ocirc; c&ugrave;ng đến trung t&acirc;m du học EduVisa n&oacute;i chung v&agrave; những người đ&atilde; gắng c&ocirc;ng gi&uacute;p đỡ em những ng&agrave;y chuẩn bị xin du học. T&ecirc;n của em l&agrave; Vũ Vạn Kim, một sinh vi&ecirc;n vừa c&oacute; được Visa Mỹ du học ngay sau khi vẫn c&ograve;n ch&acirc;n ướt ch&acirc;n r&aacute;o đến với trung t&acirc;m. Điều n&agrave;y kh&ocirc;ng những l&agrave; ước mơ của bao sinh vi&ecirc;n m&agrave; c&ograve;n l&agrave; niềm tự h&agrave;o của trung t&acirc;m, một tập thể lu&ocirc;n cố gắng để mang đến những điều tốt đẹp nhất cho kh&aacute;ch h&agrave;nh. Hơn thế nữa, phong c&aacute;ch l&agrave;m việc chuy&ecirc;n nghiệp lu&ocirc;n được c&ocirc;ng ty đặt l&ecirc;n h&agrave;ng đầu, những điều thắc mắc lu&ocirc;n được giải đ&aacute;p v&agrave; thỏa m&atilde;n. Nếu bạn muốn du học, h&atilde;y đến với trung t&acirc;m EduVisa,&nbsp; bạn lu&ocirc;n được ch&agrave;o đ&oacute;n v&agrave; nhận được những kết quả tốt m&agrave; trung t&acirc;m l&agrave;m cho bạn một c&aacute;ch nhiệt t&igrave;nh nhất.</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Vũ vạn Kim</p>', null);
INSERT INTO `du_hoc_detail` VALUES ('19', '4', 'Hoàng Mỹ Hạnh', '<p>\r\n	<img alt=\"\" src=\"/static/images/image/image034.jpg\" style=\"width: 133px; height: 187px;\" /><img alt=\"\" src=\"/static/images/image/image033.jpg\" style=\"width: 345px; height: 233px;\" /></p>\r\n<p>\r\n	Student: Ho&agrave;ng Mỹ Hạnh</p>\r\n<p>\r\n	Date of birth: 29/09/1992</p>\r\n<p>\r\n	Status : Got Visa</p>\r\n<p>\r\n	College/University: Delawave County Community College</p>\r\n<p align=\"center\">\r\n	&nbsp;</p>\r\n<p align=\"center\">\r\n	______________________</p>\r\n<p>\r\n	Em l&agrave; Ho&agrave;ng Mỹ Hạnh, 19 tuổi,&nbsp; một trong những người đ&atilde; đậu phỏng vấn du học v&agrave;o th&aacute;ng 8/2011</p>\r\n<p>\r\n	Em viết thư n&agrave;y để cảm ơn những anh chị tại trung t&acirc;m du học EduVisa đ&atilde; lo to&agrave;n bội giấy tờ du học cho em rất chu đ&aacute;o, từ việc li&ecirc;n lạc với trường b&ecirc;n Mỹ cho đến việc luyện phỏng vấn. Em biết rằng c&aacute;c anh chị đ&atilde; rất vất vả v&igrave; bộ hồ sơ của em nằm trong diện gấp r&uacute;t. Nhưng nhờ mọi người m&agrave; hồ sơ của em được ho&agrave;n th&agrave;nh kịp thời. Thật sự l&uacute;c đầu em thấy sợ v&agrave; &aacute;p lực v&igrave; những lần &ldquo; chất vấn &rdquo; trong l&uacute;c luyện phỏng vấn, nhưng thật sự n&oacute; đ&atilde; gi&uacute;p cho em c&oacute; được những phản xạ cần thiết trong buổi phỏng vấn. Trung t&acirc;m giống như ng&ocirc;i nh&agrave; thứ hai của em vậy. Khi cầm tr&ecirc;n tay Visa, em vẫn kh&ocirc;ng tin nỗi một người thiếu tự tin, nh&uacute;t nh&aacute;t v&agrave; yếu ngoại ngữ như m&igrave;nh lại c&oacute; thể đậu phỏng vấn. Tất cả l&agrave; nhờ c&oacute; c&aacute;c anh chị.</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pensylvania, ng&agrave;y 11 th&aacute;ng 11, 2011</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ho&agrave;ng Mỹ Hạnh</p>', null);
INSERT INTO `du_hoc_detail` VALUES ('20', '4', 'Tô Lâm Phượng', '<p>\r\n	<img alt=\"\" src=\"/static/images/image/image039.jpg\" style=\"width: 189px; height: 187px;\" /><img alt=\"\" src=\"/static/images/image/image038.jpg\" style=\"width: 339px; height: 227px;\" /></p>\r\n<p>\r\n	Student: T&ocirc; L&acirc;m Phượng</p>\r\n<p>\r\n	Date of birth: 15/03/1993</p>\r\n<p>\r\n	Status : Got Visa</p>\r\n<p>\r\n	College/University: Houston Community College</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p align=\"center\">\r\n	______________________</p>\r\n<p>\r\n	Hi guys ^^ !</p>\r\n<p>\r\n	M&igrave;nh l&agrave; T&ocirc; L&acirc;m Phượng, con g&aacute;i của nhạc sĩ T&ocirc; Hải, một du sinh vừa đậu phỏng vấn Mỹ kh&oacute;a 12/2011. C&aacute;c bạn biết kh&ocirc;ng, du học Mỹ l&agrave; niềm đam m&ecirc; v&agrave; ước mơ của m&igrave;nh, nhiều l&uacute;c gần như m&igrave;nh đ&atilde; bỏ cuộc nhưng nhờ c&oacute; sự động vi&ecirc;n, gi&uacute;p đỡ v&agrave; đ&agrave;o tạo của anh chị EduVisa đ&atilde; gi&uacute;p m&igrave;nh th&ecirc;m tự tin v&agrave; mạnh mẽ hơn trong quyết định.</p>\r\n<p>\r\n	Khi bước v&agrave;o l&atilde;nh sự qu&aacute;n Mỹ, d&ugrave; đ&atilde; được c&aacute;c anh chị trang bị đầy đủ kiến thức v&agrave; kinh nghiệm, m&igrave;nh vẫn cảm thấy rất run. Khi gặp l&atilde;nh sự lại c&ograve;n run hơn nữa, nh&igrave;n mặt &ocirc;ng ta rất l&agrave; nghi&ecirc;m khắc, đến tận b&acirc;y giờ nghĩ đến n&eacute;t mặt đ&oacute; m&igrave;nh vẫn c&ograve;n hơi sợ &hellip;hihi. Khi cầm được Visa Mỹ tr&ecirc;n tay người đầu ti&ecirc;n m&igrave;nh nghĩ đến l&agrave; c&aacute;c anh chị trong trung t&acirc;m EduVisa, m&igrave;nh muốn n&oacute;i lời cảm ơn ch&acirc;n th&agrave;nh đến c&aacute;c anh chị biết bao nhi&ecirc;u. Cảm ơn duy&ecirc;n số đ&atilde; đưa m&igrave;nh đến với EduVisa. Ch&uacute;c c&aacute;c anh chị c&oacute; thật nhiều sức khỏe v&agrave; ng&agrave;y c&agrave;ng vững mạnh hơn để gi&uacute;p đở c&aacute;c bạn c&oacute; mơ ước du học như em.</p>\r\n<p>\r\n	T&ocirc; L&acirc;m Phượng.</p>', null);
INSERT INTO `du_hoc_detail` VALUES ('21', '4', 'Phạm Thị Ngọc Nga', '<p>\r\n	<img alt=\"\" src=\"/static/images/image/image044.jpg\" style=\"width: 188px; height: 182px;\" /><img alt=\"\" src=\"/static/images/image/image043.jpg\" style=\"width: 355px; height: 235px;\" /></p>\r\n<p>\r\n	Student: Phạm Thị Ngọc Nga</p>\r\n<p>\r\n	Date of birth: 17/01/1992</p>\r\n<p>\r\n	Status : Got Visa</p>\r\n<p>\r\n	College/University: Houston Community College</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p align=\"center\">\r\n	______________________</p>\r\n<p>\r\n	K&iacute;nh gửi trung t&acirc;m EduVisa,</p>\r\n<p>\r\n	Em l&agrave; Phạm Thị Ngọc Nga, một người may mắn vừa c&oacute; được Visa du học Mỹ. Mọi thứ đối với em b&acirc;y giờ vẫn c&ograve;n rất mơ hồ, cứ như mơ vậy. Mới ng&agrave;y n&agrave;o c&ograve;n bở ngỡ bước đến c&ocirc;ng ty, em vẫn c&ograve;n rụt r&egrave;, e ngại v&igrave; phong c&aacute;ch v&agrave; m&ocirc;i trường l&agrave;m việc nghi&ecirc;m t&uacute;c của Trung T&acirc;m. Nhưng khi đ&atilde; được tr&ograve; chuyện v&agrave; đ&agrave;o tạo bởi c&aacute;c anh chị chuy&ecirc;n vi&ecirc;n, em thấy m&igrave;nh tự tin v&agrave; mạnh dạn hơn hẵn. Kết quả thu được sau 2 th&aacute;ng khổ luyện tại trung t&acirc;m EduVisa l&agrave; một nữ sinh vi&ecirc;n đầy tự tin v&agrave; b&atilde;n lĩnh. Sau khi c&oacute; được Visa, em vẫn kh&ocirc;ng ngừng nhận được sự hỗ trợ từ việc mua v&eacute; m&aacute;y bay đến sắp xếp chỗ ở v&agrave; xe đưa đ&oacute;n b&ecirc;n Mỹ. Thật sự em rất biết ơn c&aacute;c anh chị, em quyết định viết thư n&agrave;y thứ nhất l&agrave; để b&agrave;y tỏ l&ograve;ng biết ơn, thứ hai l&agrave; để c&aacute;c bạn c&oacute; ước muốn du học cảm thấy tin tưởng v&agrave; c&oacute; quyết định s&aacute;ng suốt khi đến với EduVisa</p>\r\n<p>\r\n	Th&acirc;n mến,</p>\r\n<p>\r\n	Phạm Thị Ngọc Nga.</p>', null);
INSERT INTO `du_hoc_detail` VALUES ('22', '4', 'Trần Thanh Tùng', '<p>\r\n	<img alt=\"\" src=\"/static/images/image/image047.jpg\" style=\"width: 303px; height: 204px;\" /></p>\r\n<p>\r\n	Student: Trần Thanh T&ugrave;ng</p>\r\n<p>\r\n	Date of birth: 18/08/1994</p>\r\n<p>\r\n	Status : Got Visa</p>\r\n<p>\r\n	College/University: Green River Community College</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p align=\"center\">\r\n	______________________</p>\r\n<p>\r\n	Washington, ng&agrave;y 02 th&aacute;ng 02 năm 2011</p>\r\n<p>\r\n	Ch&aacute;u l&agrave; Trần Thanh T&ugrave;ng, một học sinh của trường quốc tế Ch&acirc;u &Aacute; Th&aacute;i B&igrave;nh Dương. Ch&aacute;u thật sự rất biết ơn c&aacute;c ch&uacute; tại trung t&acirc;m du học EduVisa đ&atilde; biến ước mơ du học Mỹ của ch&aacute;u th&agrave;nh sự thật. Hai năm trước chị g&aacute;i của ch&aacute;u cũng đ&atilde; được c&aacute;c ch&uacute; gi&uacute;p đỡ đễ được tiếp cận với nền gi&aacute;o dục của Mỹ. Ch&aacute;u nghĩ kh&ocirc;ng chỉ c&oacute; ch&aacute;u v&agrave; gia đ&igrave;nh m&igrave;nh, m&agrave; c&ograve;n rất nhiều nhiều những bạn kh&aacute;c cũng rất muốn gửi lời cảm ơn đến c&aacute;c ch&uacute; n&ecirc;n ch&aacute;u đ&atilde; quyết định thay mặt gia đ&igrave;nh m&igrave;nh cũng như c&aacute;c bạn đ&oacute; gửi lời cảm ơn s&acirc;u sắc v&agrave; ch&acirc;n th&agrave;nh đến trung t&acirc;m EduVisa. Ch&uacute;c c&aacute;c ch&uacute; c&oacute; thật nhiều sức khỏe, th&agrave;nh c&ocirc;ng, v&agrave; lu&ocirc;n giữ vững phong độ để c&oacute; thể gi&uacute;p đỡ được nhiều bạn kh&aacute;c hơn nữa.</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trần Thanh T&ugrave;ng.</p>', null);

