INSERT INTO `info_detail` VALUES ('1', '3', 'Những Điều Cần Biết Về Hành Lý', '<h2>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Bạn rất sợ khi l&agrave;m thủ tục l&ecirc;n m&aacute;y bay phải bỏ lại h&agrave;ng h&oacute;a hay vật dụng kh&ocirc;ng được ph&eacute;p hoặc qu&aacute; qui định l&ecirc;n m&aacute;y bay. Sau đ&acirc;y l&agrave; sơ lược v&agrave;i loại h&agrave;ng h&oacute;a bạn n&ecirc;n ch&uacute; &yacute; khi đi m&aacute;y bay để hạn chế những lo lắng hay ph&aacute;t sinh kh&ocirc;ng cần thiết.</span></span></h2>\r\n<h2>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">C&oacute; 2 loại h&agrave;nh l&yacute;: h&agrave;nh l&yacute; x&aacute;ch tay (nội địa 7kg, quốc tế 10kg) v&agrave; h&agrave;nh l&yacute; k&yacute; gửi (nội địa 20kg, quốc tế 30kg).</span></span></h2>\r\n<h2>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Theo quy định, bạn kh&ocirc;ng đuợc mang theo:</span></span></h2>\r\n<ul>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Chất ăn m&ograve;n</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Kh&iacute; ga</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Chất độc</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Chất g&acirc;y nổ</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">H&agrave;ng ho&aacute; nguy hiểm kh&aacute;c</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Chất l&acirc;y nhiếm (m&aacute;u, trứng gia cầm trong c&aacute;c đợt c&oacute; dịch, mắm t&ocirc;m)</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Chất lỏng dễ ch&aacute;y (sơn, sửa rửa mặt, kem trị mụn, dầu gội đầu...)</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Chất oxit hữu cơ dễ ch&aacute;y</span><span style=\"font-size: 12px;\"> </span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Chất ph&oacute;ng xạ</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Chất rẵn dễ ch&aacute;y</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Chất oxy ho&aacute; dễ ch&aacute;y (b&igrave;nh ga mini, chất tẩy...)</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Vật c&oacute; từ t&iacute;nh cao (nam ch&acirc;m kh&ocirc;ng nằm trong thiết bị đang sử dụng th&ocirc;ng thường,...)</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">C&aacute;c chai lọ c&oacute; &aacute;p suất cao (như b&igrave;nh xịt t&oacute;c)</span></span></h2>\r\n	</li>\r\n	<li>\r\n		<h2>\r\n			<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Vũ kh&iacute;, vật nhọn, s&uacute;ng đạn</span></span></h2>\r\n	</li>\r\n</ul>\r\n<h2>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">C&aacute;c quy định n&agrave;y đặc biệt nghi&ecirc;m ngặt tr&ecirc;n c&aacute;c chuyến bay quốc tế nhằm hạn chế khủng bố v&agrave; c&aacute;c rắc rối li&ecirc;n quan. Tr&ecirc;n đường bay nội địa việc kiểm so&aacute;t c&oacute; t&iacute;nh &quot;linh động&quot; hơn. Thỉnh thoảng bạn cũng c&oacute; thể mang theo mỹ phẩm, c&aacute;c loại kem (kem rửa mặt, dưỡng da, trị mụn....). Nhưng bạn n&ecirc;n hạn chế v&agrave; đặc biệt kh&ocirc;ng n&ecirc;n k&yacute; gửi theo h&agrave;nh l&yacute; v&igrave; h&agrave;nh l&yacute; k&yacute; gửi thường bị va chạm mạnh, chất lỏng c&oacute; thể l&agrave;m hư những đồ đạc kh&aacute;c để chung.</span></span></h2>\r\n<h2>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Với những đồ qu&yacute; gi&aacute;:</span></span></h2>\r\n<p>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">V&agrave; đ&acirc;y l&agrave; những m&oacute;n đồ bạn kh&ocirc;ng n&ecirc;n gửi k&egrave;m h&agrave;nh l&yacute; k&yacute; gửi v&igrave; &quot;dễ kh&ocirc;ng c&aacute;nh m&agrave; bay&quot;, khi bị mất chỉ được đền b&ugrave; theo... &quot;k&yacute;-l&ocirc;-gam&quot;:</span></span></p>\r\n<ul>\r\n	<li>\r\n		<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">M&aacute;y t&iacute;nh, v&agrave;ng bạc, camera, notebook, thiết bị đị&ecirc;n tử đắt tiền...</span></span></li>\r\n	<li>\r\n		<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Chai lọ....</span></span></li>\r\n	<li>\r\n		<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Đồ cổ...</span></span></li>\r\n	<li>\r\n		<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Đồng hồ đeo tay....</span></span></li>\r\n</ul>\r\n<p>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Nếu c&aacute;c m&oacute;n đồ tr&ecirc;n gọn nhẹ, bạn n&ecirc;n c&agrave;m theo người. Nhưng ch&uacute; &yacute; tắt (power off) trong khi bay.</span></span></p>\r\n<h2>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Với chất lỏng:</span></span></h2>\r\n<p>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Mối h&agrave;nh kh&aacute;ch được mang 100ml (1 l&iacute;t) chất lỏng l&ecirc;n m&aacute;y bay. Chất lỏng gồm: đồ uống, kem, nước hoa, b&igrave;nh xịt, kem đ&aacute;nh răng, son m&ocirc;i, c&aacute;c chất tương tự.</span></span></p>\r\n<p>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Điều kiện: dung t&iacute;ch c&aacute;c chai &lt;=1 l&iacute;t, c&oacute; nắp đậy k&iacute;n, tất cả g&oacute;i gọn trong t&uacute;i nhựa trong suốt v&agrave; mị&ecirc;ng t&uacute;i c&oacute; thể mở ra được, phải xuất tr&igrave;nh cho an ninh s&acirc;n bay khi l&agrave;m thủ tục.</span></span></p>\r\n<p>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Được mang chất lỏng l&agrave;: thuốc uống, đồ ăn ki&ecirc;ng đặc biệt phục vụ chữa bệnh, thức ăn cho trẻ. Nhưng thuốc phải c&oacute; k&egrave;m theo đơn chỉ định của b&aacute;c sĩ.Tốt nhất c&aacute;c bạn kh&ocirc;ng n&ecirc;n mang theo chất lỏng v&igrave; bộ phận kiểm tra ở s&acirc;n bay&nbsp;kh&ocirc;ng c&oacute; thời gian kiểm tra chi tiết những thứ bạn mang theo, đặc biệt khi bạn đến trễ. Khi thấy h&agrave;nh kh&aacute;ch mang theo những thứ n&agrave;y, họ thường nhắc bạn bỏ lại. Đặc biệt l&agrave; rượu, nước hoa chai lớn....</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\"><span style=\"font-size: 12px;\">Tips: Nếu cần bạn n&ecirc;n mang theo thức ăn v&agrave; nước uống, ra s&acirc;n bay sớm v&agrave; l&agrave;m thủ tục sau đ&oacute; nh&acirc;m nhi tại ph&ograve;ng chờ. Thức ăn v&agrave; nước uống b&aacute;n ở s&acirc;n bay thường đắt gấp 2,3 lần so với ở ngo&agrave;i.</span></span></p>', '#', '0');
INSERT INTO `info_detail` VALUES ('2', '2', 'Mua Vé Máy Bay - Những Điều Cần Biết', '<p>\r\n	Trong thời buổi cạnh tranh giữa c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng, việc đi lại bằng m&aacute;y bay ng&agrave;y c&agrave;ng trở n&ecirc;n dễ d&agrave;ng v&agrave; th&ocirc;ng dụng hơn. Chẳng b&ugrave; cho mươi năm về trước, m&aacute;y bay chỉ d&agrave;nh cho c&aacute;c vi&ecirc;n chức cao cấp c&oacute; đầy đủ giấy tờ đi c&ocirc;ng t&aacute;c hoặc những người lắm tiền mới gi&agrave;nh được một chổ ngồi tr&ecirc;n c&aacute;c m&aacute;y bay AN hoặc TU cũ kỹ do Li&ecirc;n X&ocirc; sản xuất.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Ng&agrave;y nay, với hơn 20&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;c&oacute; mặt ở Việt Nam va` nhiều ch&iacute;nh s&aacute;ch gi&aacute; cạnh tranh, hầu như ai cũng c&oacute; thể dễ d&agrave;ng book một chổ tr&ecirc;n&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/may-bay-2/tin-du-lich.html\" title=\"Từ khóa: máy bay\">m&aacute;y bay</a>&nbsp;th&ocirc;ng qua một đại l&yacute; của c&aacute;c&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;v&agrave; sau đ&oacute; trả tiền để lấy v&eacute; v&agrave; chờ đến ng&agrave;y l&ecirc;n&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/may-bay-2/tin-du-lich.html\" title=\"Từ khóa: máy bay\">m&aacute;y bay</a>. Mọi chuyện xem c&oacute; vẻ dễ d&agrave;ng nhưng nếu kh&ocirc;ng &ldquo;pro-chuy&ecirc;n nghiệp&rdquo; một t&iacute; th&igrave; coi chừng đấy, bạn sẽ gặp bao nhi&ecirc;u chuyện bực m&igrave;nh tr&ecirc;n đường đi. Xin m&aacute;ch bạn một v&agrave;i kiến thức chuy&ecirc;n s&acirc;u về h&agrave;ng kh&ocirc;ng để tận hưởng một&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/chuyen-bay/tin-du-lich.html\" title=\"Từ khóa: chuyến bay\">chuyến bay</a>&nbsp;thật thoải m&aacute;i.&nbsp;</p>\r\n<p>\r\n	1)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Đặt chỗ:</strong><br />\r\n	Muốn đi ma&yacute; bay, bạn phải đến một đại l&yacute; h&agrave;ng kh&ocirc;ng (hoặc điện thoại đến) để đặt chổ trước. Bạn n&ecirc;n biết khi đặt chổ (n&oacute;i n&ocirc;m na l&agrave; bỏ &hellip;cục gạch để l&agrave;m dấu: chổ n&agrave;y t&ocirc;i đ&atilde; x&iacute; phần rồi đấy&hellip; đừng c&oacute; đụng v&agrave;o) bạn kh&ocirc;ng phải&hellip;trả tiền. V&igrave; vậy bạn n&ecirc;n đặt chổ trước. V&iacute; nế&ugrave; ng&agrave;y bay của bạn rơi v&agrave;o c&aacute;c dịp cao điểm như lễ, tết, h&egrave;&hellip;nếu kh&ocirc;ng đặt chổ trước th&igrave; bạn chỉ c&oacute; nước đi &hellip;t&agrave;u hoặc xe đ&ograve; với gi&aacute; chợ đen hoăc ngậm ng&ugrave;i nằmnh&agrave; m&agrave; r&uacute;t kinh nghiệm cho lần sau .. Khiđặt chổ, bạn phải cho t&ecirc;n đ&uacute;ng theo chứng minh nh&acirc;n d&acirc;n hoặc hộ chiếu N&ecirc;u v&eacute; sai t&ecirc;n với CMND hoặc hộ chiếu, nh&acirc;n vi&ecirc;n h&agrave;ng kh&ocirc;ng sẽ kh&ocirc;ng cho bạn l&agrave;m thủ tục bay .&nbsp;<br />\r\n	<br />\r\n	Bạn sẽ mất th&ecirc;m tiền v&agrave; thời gian mua 1 v&eacute; moi .Chưa kể đến việc ho&agrave;n lai v&eacute; cũ phai mất &iacute;t nhất từ USD25 đến USD 75 &mdash;đ&oacute; l&agrave; nếu v&eacute; ho&agrave;n được .C&ograve;n kh&ocirc;ng th&igrave; trong t&igrave;nh huống xấu nhất ,nếu bạn đi du lịch theo đ&ograve;an th&igrave; ph&iacute; ho&agrave;n v&eacute; l&agrave; 25%-30% gi&aacute; tri ve&rsquo; , Bạn n&ecirc;n cho số điện thoại để đại l&yacute; c&oacute; thể li&ecirc;n hệ với bạn trong trường hợp&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/chuyen-bay/tin-du-lich.html\" title=\"Từ khóa: chuyến bay\">chuyến bay</a>&nbsp;của bạn đặt chổ bị hủy hoặc đổi giờ bay.Đại l&yacute; sẽ đưa cho bạn một tờ giữ chổ (gọi l&agrave; booking) tr&ecirc;n đ&oacute; c&oacute; t&ecirc;n của người đi, ng&agrave;y bay, số hiệu&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/chuyen-bay/tin-du-lich.html\" title=\"Từ khóa: chuyến bay\">chuyến bay</a>, giờ cất c&aacute;nh v&agrave; hạ c&aacute;nh v&agrave; quan trọng hơn cả l&agrave; thời hạn cuối c&ugrave;ng bạn phải mua v&eacute;. Nếu qu&aacute; thời hạn m&agrave; bạn chưa mua v&eacute;, chổ bạn đặt l&acirc;u nay sẽ tự động mất (h&agrave;ng kh&ocirc;ng sẽ lấy chổ của bạn b&aacute;n cho người kh&aacute;c). Khi mua v&eacute;, bạn chỉ việc đưa booking n&agrave;y cho đại l&yacute; để họ xuất v&eacute; cho bạn. Nếu kh&ocirc;ng đi được , bạn n&ecirc;n gọi lại cho n&ocirc;i book v&eacute; để hủy chồ hoăc dời sang ng&agrave;y kh&aacute;c .H&atilde;y book chổ trước, cho t&ecirc;n ch&iacute;nh x&aacute;c v&agrave; lấy v&eacute; đ&uacute;ng thời hạn !&nbsp;</p>\r\n<p>\r\n	2)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<strong><a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">H&atilde;ng h&agrave;ng kh&ocirc;ng</a></strong><br />\r\n	Hiện nay c&oacute; rất nhiều&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;để bạn lựa chọn: trong nước th&igrave; c&oacute; Vietnam Airlines, Pacific Airlines. Nước ngo&agrave;i th&igrave; c&oacute; Cathay Pacific, Singapore Airlines, Thai Airways, Eva Airways , China airlines , Japan Airlines &hellip;Khi bạn chọn 1&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;để mua v&eacute; th&igrave; bạn đ&atilde; c&oacute; một quyết định kh&aacute; l&agrave; quan trọng. N&oacute; sẽ li&ecirc;n quan đến:&bull; Gi&aacute; v&eacute;: C&aacute;c&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;c&agrave;ng nổi tiếng th&igrave; gi&aacute; v&eacute; c&agrave;ng cao (ngoại trừ c&aacute;c&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;đang độc quyền khai th&aacute;c một đường bay n&agrave;o đ&oacute;).&nbsp;<br />\r\n	<br />\r\n	Gi&aacute; v&eacute; tr&ecirc;n c&ugrave;ng một chặng bay của một&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;nổi tiếng c&oacute; khi đắt gần&hellip; gấp đ&ocirc;i gi&aacute; v&eacute; của một h&agrave;ng h&agrave;ng kh&ocirc;ng&hellip; mới nghe t&ecirc;n lần đầu.</p>\r\n<p style=\"margin-left: 7.5pt;\">\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Dịch vụ: Gi&aacute; v&eacute; c&agrave;ng cao th&igrave; kh&aacute;ch h&agrave;ng c&agrave;ng thực sự l&agrave; thượng đế. Gi&aacute; v&eacute; qu&aacute; thấp một phần l&agrave; do&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;đ&atilde; cắt bỏ hầu hết c&aacute;c dịch vụ tối thiểu m&agrave; bạn phải c&oacute; tr&ecirc;n<a href=\"http://www.chudu24.com/tu-khoa-du-lich/chuyen-bay/tin-du-lich.html\" title=\"Từ khóa: chuyến bay\">chuyến bay</a>. Bạn sẽ cảm nhận được sự kh&aacute;c biệt nếu bạn đ&atilde; từng bay với nhiều&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;kh&aacute;c nhau. Với&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;n&agrave;y, bạn chưa kịp cởi &aacute;o vest ra th&igrave; đ&atilde; c&oacute; ngay tiếp vi&ecirc;n xuất hiện b&ecirc;n cạnh cầm lấy &aacute;o vest của bạn v&agrave; đem m&oacute;c v&agrave;o tủ &aacute;o. Với h&atilde;ng kh&aacute;c, khi cảm thấy qu&aacute; lạnh, bạn c&oacute; bấm chu&ocirc;ng, y&ecirc;u cầu, xin xỏ mct c&aacute;i mền cũng v&ocirc; &iacute;ch v&igrave; &ldquo;sorry, we have no more blanket for you&rdquo; (xin lổi, ch&uacute;ng t&ocirc;i kh&ocirc;ng c&ograve;n mền cho bạn).</p>\r\n<p style=\"margin-left: 7.5pt;\">\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; An to&agrave;n : Gi&aacute; v&eacute; cao v&igrave; trong đ&oacute; c&oacute;&hellip;bảo hiểm cao. Bảo hiểm cao cũng c&oacute; nghĩa l&agrave; phương tiện c&oacute; độ an to&agrave;n cao. Đ&acirc;y l&agrave; điều &iacute;t người biết đến hoặc kh&ocirc;ng muốn nhắc đến v&igrave; ai m&agrave; muốn n&oacute;i đến chuyện rủi ro khi đi xa.. Một&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>, sau một tai nạn&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/may-bay-2/tin-du-lich.html\" title=\"Từ khóa: máy bay\">m&aacute;y bay</a>, số kh&aacute;ch đ&atilde; kh&ocirc;ng giảm m&agrave; lại c&agrave;ng tăng cao v&agrave; chỉ số cổ phiếu của h&atilde;ng tăng vọt l&agrave; nhờ tiền bảo hiểm trả cho c&aacute;c nạn nh&acirc;n kh&aacute; cao. Một đo&agrave;n kh&aacute;ch du lịch ở Ch&acirc;u Au đ&atilde; phải trả gi&aacute; đắt bằng sinh mạng của m&igrave;nh khi mua v&eacute; ở một&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;kh&ocirc;ng t&ecirc;n tuổi . H&atilde;y book v&eacute; ờ đại l&yacute; c&oacute; uy t&iacute;n v&agrave; mua v&eacute; với c&aacute;c&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hang-hang-khong/tin-du-lich.html\" title=\"Từ khóa: hãng hàng không\">h&atilde;ng h&agrave;ng kh&ocirc;ng</a>&nbsp;t&ecirc;n tuổi !</p>\r\n<p>\r\n	3)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<strong><a href=\"http://www.chudu24.com/tu-khoa-du-lich/hanh-trinh-bay/tin-du-lich.html\" title=\"Từ khóa: hành trình bay\">H&agrave;nh tr&igrave;nh bay</a></strong>&nbsp;:<br />\r\n	Từ TP Hồ Ch&iacute; Minh, nếu bạn bay gần th&igrave; c&oacute;&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/nha-trang/tin-du-lich.html\" title=\"Từ khóa: nha trang\">Nha Trang</a>,&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/da-lat/tin-du-lich.html\" title=\"Từ khóa: đà lạt\">Đ&agrave; lạt</a>, trung b&igrave;nh th&igrave;&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/ha-noi/tin-du-lich.html\" title=\"Từ khóa: hà nội\">H&agrave; Nội</a>,&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/bangkok/tin-du-lich.html\" title=\"Từ khóa: bangkok\">Bangkok</a>, xa th&igrave;&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/seoul/tin-du-lich.html\" title=\"Từ khóa: Seoul\">Seoul</a>,&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/tokyo/tin-du-lich.html\" title=\"Từ khóa: Tokyo\">Tokyo</a>&nbsp;v&agrave; phức tạp hơn th&igrave; c&oacute; Bắc Kinh, Paris, Amsterdam, Newyork, Los&hellip;Tr&ecirc;n l&yacute; thuyết, những chặng bay gần v&agrave; trung b&igrave;nh thường l&agrave; những&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/chuyen-bay/tin-du-lich.html\" title=\"Từ khóa: chuyến bay\">chuyến bay</a>&nbsp;thẳng. Những chặng bay xa thường sẽ phải qu&aacute; cảnh hay n&oacute;i n&ocirc;m na l&agrave; phải dừng (từ chuy&ecirc;n m&ocirc;n gọi l&agrave; transit) tại một hoặc nhiều&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/san-bay/tin-du-lich.html\" title=\"Từ khóa: sân bay\">s&acirc;n bay</a>: dừng để lấy th&ecirc;m kh&aacute;ch rồi bay tiếp tr&ecirc;n c&ugrave;ng&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/chuyen-bay/tin-du-lich.html\" title=\"Từ khóa: chuyến bay\">chuyến bay</a>&nbsp;hoặc dừng để chuyển qua một&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/may-bay-2/tin-du-lich.html\" title=\"Từ khóa: máy bay\">m&aacute;y bay</a>&nbsp;kh&aacute;c.&nbsp;<br />\r\n	<br />\r\n	Nhưng d&ugrave; dừng theo c&aacute;ch n&agrave;o đi nữa cũng l&agrave;m bạn rất mệt (v&igrave; phải hạ c&aacute;nh v&agrave; cất c&aacute;nh nhiều lần) v&agrave; mất thời gian (đ&ocirc;i l&uacute;c bạn phải chờ đến 7, 8 tiếng để đi tiếp. C&oacute; khi phải chờ qua đ&ecirc;m ở <a href=\"http://www.chudu24.com/tu-khoa-du-lich/san-bay/tin-du-lich.html\" title=\"Từ khóa: sân bay\">s&acirc;n bay</a>&nbsp;để s&aacute;ng sớm h&ocirc;m sau tiếp tục h&agrave;nh tr&igrave;nh). Đối với c&aacute;c chặng bay trung b&igrave;nh, c&aacute;c bạn cũng đừng chủ quan v&igrave; nhiều khi để b&aacute;n cho được v&eacute;, c&aacute;c đại l&yacute; c&oacute; thể cho bạn đi&hellip;l&ograve;ng v&ograve;ng m&agrave; chỉ khi&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/may-bay-2/tin-du-lich.html\" title=\"Từ khóa: máy bay\">m&aacute;y bay</a>&nbsp;cất c&aacute;nh th&igrave; bạn mới ph&aacute;t hiện ra được. C&oacute; bạn ở TP Hồ Ch&iacute; Minh mua v&eacute; đi <a href=\"http://www.chudu24.com/tu-khoa-du-lich/ha-noi/tin-du-lich.html\" title=\"Từ khóa: hà nội\">H&agrave; Nội</a>&nbsp;lại được tham quan&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/san-bay/tin-du-lich.html\" title=\"Từ khóa: sân bay\">s&acirc;n bay</a>&nbsp;Bu&ocirc;n M&ecirc; Thuột v&agrave; đi Kuala Lumpur m&agrave; biết th&ecirc;m&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/san-bay/tin-du-lich.html\" title=\"Từ khóa: sân bay\">s&acirc;n bay</a>Changi nổi tiếng của Singapore l&agrave; chuyện ..kh&ocirc;ng mới</p>\r\n<p style=\"margin-left: 7.5pt;\">\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; H&atilde;y chọn&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/hanh-trinh-bay/tin-du-lich.html\" title=\"Từ khóa: hành trình bay\">h&agrave;nh tr&igrave;nh bay</a>&nbsp;thẳng cho chặng bay ngắn v&agrave; trung b&igrave;nh !</p>\r\n<p style=\"margin-left: 7.5pt;\">\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; H&atilde;y tr&aacute;nh &ldquo;transit&rdquo; được chừng n&agrave;o tốt chừng đ&oacute; đối với chặng bay xa !</p>\r\n<p>\r\n	4)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<strong><a href=\"http://www.chudu24.com/tu-khoa-du-lich/loai-may-bay/tin-du-lich.html\" title=\"Từ khóa: loại máy bay\">Loại&nbsp;</a><a href=\"http://www.chudu24.com/tu-khoa-du-lich/may-bay-2/tin-du-lich.html\" title=\"Từ khóa: máy bay\">m&aacute;y bay</a></strong><br />\r\n	Nếu bay tr&ecirc;n một h&agrave;nh tr&igrave;nh c&oacute; nhiều&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/chuyen-bay/tin-du-lich.html\" title=\"Từ khóa: chuyến bay\">chuyến bay</a>&nbsp;với nhiều&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/loai-may-bay/tin-du-lich.html\" title=\"Từ khóa: loại máy bay\">loại&nbsp;</a><a href=\"http://www.chudu24.com/tu-khoa-du-lich/may-bay-2/tin-du-lich.html\" title=\"Từ khóa: máy bay\">m&aacute;y bay</a>&nbsp;kh&aacute;c nhau th&igrave; bạn n&ecirc;n chọn&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/loai-may-bay/tin-du-lich.html\" title=\"Từ khóa: loại máy bay\">loại&nbsp;</a><a href=\"http://www.chudu24.com/tu-khoa-du-lich/may-bay-2/tin-du-lich.html\" title=\"Từ khóa: máy bay\">m&aacute;y bay</a>&nbsp;lớn: bay nhanh hơn, an to&agrave;n hơn Airbus, Boeing sẽ tốt hơn l&agrave; Folker, ATR hoặc MD 8. Nhưng nếu bạn bay đến c&aacute;c&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/san-bay/tin-du-lich.html\" title=\"Từ khóa: sân bay\">s&acirc;n bay</a>&nbsp;nhỏ th&igrave; xin lỗi bạn: kh&ocirc;ng c&ograve;n chọn lựa n&agrave;o kh&aacute;c (no choice) H&atilde;y chọn&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/loai-may-bay/tin-du-lich.html\" title=\"Từ khóa: loại máy bay\">loại&nbsp;</a><a href=\"http://www.chudu24.com/tu-khoa-du-lich/may-bay-2/tin-du-lich.html\" title=\"Từ khóa: máy bay\">m&aacute;y bay</a>&nbsp;lớn, hiện đại !&nbsp;</p>\r\n<p>\r\n	<strong>&nbsp;Giờ bay v&agrave; giờ đến</strong><strong>:</strong><br />\r\n	<br />\r\n	<strong>Bạn phải lưu &yacute; đến giờ bay v&agrave; giờ đ&aacute;p cho ph&ugrave; hợp với thời gian v&agrave; mục đ&iacute;ch chuyến đi. C&ugrave;ng bay đến&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/bangkok/tin-du-lich.html\" title=\"Từ khóa: bangkok\">Bangkok</a>, c&oacute; h&atilde;ng bay l&uacute;c 12:00 nhưng cũng c&oacute; h&atilde;ng bay l&uacute;c 20:30.&nbsp;<br />\r\n	<br />\r\n	Nếu c&oacute; transit, bạn cần lưu &yacute; đến giờ bay hơn nữa. Cố gắng đừng để thời gian chờ bay chuyến tiếp theo qu&aacute; d&agrave;i (ngồi l&acirc;y lất ở&nbsp;<a href=\"http://www.chudu24.com/tu-khoa-du-lich/san-bay/tin-du-lich.html\" title=\"Từ khóa: sân bay\">s&acirc;n bay</a>&nbsp;rất&hellip;mệt) hoặc qu&aacute; ngắn (bạn phải chạy marathon từ nh&agrave; ga 1 để nối chuyến kế tiếp tại nh&agrave; ga 2 v&agrave; chắc chắn một điều l&agrave; h&agrave;nh l&yacute; của bạn sẽ đến sau v&igrave; h&agrave;nh l&yacute; th&igrave; kh&ocirc;ng thể &hellip;chạy marathon như bạn.</strong></p>', '#', '0');
INSERT INTO `info_detail` VALUES ('3', '3', 'Những điều cần biết khi quá cảnh', '<p>\r\n	<strong>1. T&igrave;m hiểu thật kỹ c&agrave;ng th&ocirc;ng tin tại ph&ograve;ng v&eacute;:</strong></p>\r\n<p>\r\n	<br />\r\n	Th&ocirc;ng thường, bạn mua được v&eacute; m&aacute;y bay c&oacute; cuộc h&agrave;nh tr&igrave;nh thẳng v&agrave; kh&ocirc;ng phải qu&aacute; cảnh th&igrave; đ&oacute; l&agrave; một điều đ&aacute;ng ch&uacute;c mừng cho chuyến đi của bạn (mặc d&ugrave; gi&aacute; v&eacute; m&aacute;y bay c&oacute; rẻ hơn gi&aacute; v&eacute; m&aacute;y bay thẳng). Nhưng đ&ocirc;i khi v&agrave;o m&ugrave;a cao điểm hoặc từ s&acirc;n bay nơi bạn xuất ph&aacute;t kh&ocirc;ng c&oacute; đường bay thẳng th&igrave; c&aacute;c đường bay c&oacute; qu&aacute;&nbsp; cảnh sẽ l&agrave; cứu c&aacute;nh duy nhất trong l&uacute;c đ&oacute;. Nếu rơi v&agrave;o trường hợp n&agrave;y, bạn n&ecirc;n lưu &yacute; một số th&ocirc;ng tin sau:<br />\r\n	<br />\r\n	- Khi mua v&eacute; phải qu&aacute; cảnh, bạn n&ecirc;n hỏi kỹ nh&acirc;n vi&ecirc;n ph&ograve;ng v&eacute; rằng thời gian bạn phải qu&aacute; cảnh l&agrave; bao l&acirc;u. Th&ocirc;ng thường, nh&acirc;n vi&ecirc;n ph&ograve;ng v&eacute; sẽ t&igrave;m cho bạn những chuyến bay qu&aacute; cảnh ngắn nhất nhưng đ&ocirc;i khi họ cũng c&oacute; thể qu&ecirc;n điều n&agrave;y. L&uacute;c n&agrave;y, bạn n&ecirc;n chủ động y&ecirc;u cầu để c&oacute; thể tiết kiệm thời gian của chuyến bay nhất (nếu c&oacute; thể).<br />\r\n	<br />\r\n	- Trong một v&agrave;i trường hợp, thời gian qu&aacute; cảnh qu&aacute; d&agrave;i, c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng sẽ sắp xếp cho h&agrave;nh kh&aacute;ch đi nghỉ ở c&aacute;c kh&aacute;ch sạn để chờ chuyến bay chuyển tiếp. Nếu như thế, bạn sẽ hỏi họ m&igrave;nh sẽ ở kh&aacute;ch sạn n&agrave;o? Li&ecirc;n hệ với ai tại s&acirc;n bay qu&aacute; cảnh? Bạn c&oacute; phải trả th&ecirc;m chi ph&iacute; n&agrave;o cho chuyến bay nữa kh&ocirc;ng?... Những th&ocirc;ng tin n&agrave;y l&agrave; hết sức quan trọng để tr&aacute;nh những sự việc kh&ocirc;ng hay xảy ra tại s&acirc;n bay qu&aacute; cảnh.</p>\r\n<p>\r\n	<strong>2. Quan s&aacute;t cẩn thận c&aacute;c biển chỉ dẫn tại s&acirc;n bay qu&aacute; cảnh</strong></p>\r\n<p>\r\n	<br />\r\n	Khi m&aacute;y bay đ&aacute;p xuống s&acirc;n bay qu&aacute; cảnh, l&uacute;c n&agrave;y, bạn phải tự lực c&aacute;nh sinh để bắt chuyến bay kế tiếp. Thế n&ecirc;n, việc theo d&otilde;i c&aacute;c chỉ dẫn tại s&acirc;n bay qu&aacute; cảnh l&agrave; cực kỳ quan trọng. Tại c&aacute;c ga đến của s&acirc;n bay thường c&oacute; hai lối cho h&agrave;nh kh&aacute;ch l&agrave;: Transit v&agrave; Immigrant, trong trường hợp n&agrave;y, bạn sẽ đi theo cổng Transit v&agrave; qua soi chiếu an ninh để qu&aacute; cảnh cho chuyến bay kế tiếp.<br />\r\n	<br />\r\n	Th&ocirc;ng thường, khi qu&aacute; cảnh, bạn sẽ được ph&aacute;t số v&eacute; m&aacute;y bay tương ứng với số s&acirc;n bay bạn đến. V&eacute; xuất ph&aacute;t sẽ ghi r&otilde; r&agrave;ng cổng ra m&aacute;y bay v&agrave; số ghế tr&ecirc;n m&aacute;y bay. Tuy nhi&ecirc;n, v&eacute; d&ugrave;ng để qu&aacute; cảnh sẽ chỉ c&oacute; số ghế m&aacute;y bay m&agrave; kh&ocirc;ng c&oacute; th&ocirc;ng tin về cổng ra m&aacute;y bay. Bạn đừng qu&aacute; lo lắng về việc n&agrave;y. Bởi v&igrave; tại s&acirc;n bay qu&aacute; cảnh, th&ocirc;ng tin về cổng ra m&aacute;y bay sẽ được hiển thị cụ thể tr&ecirc;n c&aacute;c bảng điện tử. Việc của bạn l&agrave; nắm r&otilde; số hiệu chuyến bay của m&igrave;nh v&agrave; đối chiếu với bản điện tử, sau đ&oacute;, bạn sẽ biết được c&aacute;c th&ocirc;ng tin về giờ bay, cổng ra m&aacute;y bay thật ch&iacute;nh x&aacute;c.<br />\r\n	<br />\r\n	Nếu bạn biết sử dụng tiếng Anh th&igrave; khi c&oacute; rắc rối bạn c&oacute; thể hỏi c&aacute;c nh&acirc;n vi&ecirc;n s&acirc;n bay để được hướng dẫn cụ thể. Nếu bạn kh&ocirc;ng biết d&ugrave;ng tiếng Anh cũng đừng qu&aacute; lo lắng. Trước ng&agrave;y bay, bạn n&ecirc;n nhờ một người biết tiếng Anh ghi ra giấy cho bạn một số c&acirc;u hỏi t&igrave;nh huống c&oacute; thể gặp như: &ldquo;Cổng ra m&aacute;y bay ở chỗ n&agrave;o?&rdquo;, &ldquo;Nh&agrave; vệ sinh ở đ&acirc;u?&rdquo;, &hellip;C&aacute;c nh&acirc;n vi&ecirc;n s&acirc;n bay qu&aacute; cảnh sẽ vui l&ograve;ng gi&uacute;p đỡ bạn với th&aacute;i độ tận t&igrave;nh nhất.</p>\r\n<p>\r\n	<strong>3. H&agrave;nh l&yacute; x&aacute;ch tay gọn nhẹ:</strong></p>\r\n<p>\r\n	<br />\r\n	Bạn n&ecirc;n biết rằng, khi qu&aacute; cảnh, khoảng c&aacute;ch từ khi bạn bước xuống m&aacute;y bay đi l&ograve;ng v&ograve;ng để t&igrave;m cổng ra m&aacute;y bay rồi l&ecirc;n m&aacute;y bay cho chuyến bay kế tiếp l&agrave; rất d&agrave;i. Nếu bạn x&aacute;ch một h&agrave;nh l&yacute; x&aacute;ch tay qu&aacute; nặng c&oacute; thể l&agrave;m cho bạn hao tốn rất nhiều sức lực v&agrave; lại rất bất tiện. Bạn chỉ n&ecirc;n mang một c&aacute;i ba-l&ocirc; nhỏ với những vật dụng c&aacute; nh&acirc;n thật sự cần thiết.<br />\r\n	<br />\r\n	Ngo&agrave;i ra, trong h&agrave;nh l&yacute; x&aacute;ch tay, bạn cũng n&ecirc;n chuẩn bị một &iacute;t thức ăn nhẹ như: b&aacute;nh quy, sandwich, &hellip; Đồng thời, bạn cũng n&ecirc;n mang theo một chai nước suối nhỏ. L&yacute; do l&agrave; v&igrave; c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng sẽ chỉ cung cấp cho bạn thức ăn tr&ecirc;n m&aacute;y bay, c&ograve;n khi qu&aacute; cảnh bạn sẽ phải tự t&uacute;c cho việc ăn uống. Nếu thời gian qu&aacute; cảnh d&agrave;i m&agrave; kh&ocirc;ng chuẩn bị thức ăn, bạn c&oacute; thể sẽ bị đ&oacute;i. Trong khi đ&oacute;, thức ăn b&aacute;n tại c&aacute;c s&acirc;n bay thường c&oacute; gi&aacute; rất đắt v&agrave; đ&ocirc;i khi c&ograve;n kh&ocirc;ng hợp với khẩu vị của bạn.</p>\r\n<p>\r\n	<strong>4. Mang theo laptop để giết thời gian:</strong></p>\r\n<p>\r\n	<br />\r\n	Khi phải ngồi đợi ở s&acirc;n bay qu&aacute; cảnh l&acirc;u, bạn sẽ nhanh ch&oacute;ng bị buồn ch&aacute;n. L&uacute;c n&agrave;y, nếu c&oacute; mang theo laptop th&igrave; bạn c&oacute; thể d&ugrave;ng n&oacute; để giải quyết c&ocirc;ng việc hay giải tr&iacute; trong thời gian chờ đợi.<br />\r\n	<br />\r\n	Th&ocirc;ng thường, c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng sẽ cho bạn mang theo laptop l&ecirc;n m&aacute;y bay m&agrave; kh&ocirc;ng hề t&iacute;nh th&ecirc;m c&acirc;n nặng. Ngo&agrave;i ra, hiện nay, hầu hết c&aacute;c s&acirc;n bay tr&ecirc;n thế giới cũng đều c&oacute; hệ thống wifi gi&uacute;p bạn sử dụng internet một c&aacute;ch dễ d&agrave;ng. Bạn c&oacute; thể d&ugrave;ng laptop để tr&ograve; chuyện c&ugrave;ng bạn b&egrave;, đọc b&aacute;o trong khi chờ chuyến bay kế tiếp.</p>\r\n<p>\r\n	<strong>5. Mua h&agrave;ng miễn thuế tại c&aacute;c s&acirc;n bay qu&aacute; cảnh:</strong></p>\r\n<p>\r\n	<br />\r\n	Để phục vụ h&agrave;nh kh&aacute;ch qu&aacute; cảnh, c&aacute;c s&acirc;n bay tr&ecirc;n thế giới thường c&oacute; hệ thống cửa h&agrave;ng duty-free rất ho&agrave;nh tr&aacute;ng v&agrave; hấp dẫn. Ở một số s&acirc;n bay lớn, c&aacute;c cửa h&agrave;ng n&agrave;y sẽ c&oacute; b&aacute;n đầy đủ c&aacute;c loại h&agrave;ng thời trang với nh&atilde;n hiệu nổi tiếng thế giới, c&aacute;c loại rượu ngoại hấp dẫn, những m&oacute;n đồ chơi nhiều m&agrave;u sắc. H&agrave;nh kh&aacute;ch, nhất l&agrave; c&aacute;c qu&yacute; b&agrave; sẽ kh&oacute; cầm l&ograve;ng với thi&ecirc;n đường shopping n&agrave;y.<br />\r\n	<br />\r\n	Thế nhưng, bạn h&atilde;y nhớ rằng, chỉ n&ecirc;n mua h&agrave;ng tại c&aacute;c cửa h&agrave;ng duty-free khi thật sự cần thiết. V&igrave; khi b&aacute;n h&agrave;ng cho bạn, c&aacute;c nh&acirc;n vi&ecirc;n tại đ&acirc;y sẽ thường nhận thanh to&aacute;n bằng USD hay Euro nhưng khi thối lại tiền thừa cho bạn họ sẽ thối lại tiền của bản xứ. Kết quả l&agrave; bạn sẽ phải &ocirc;m một lượng tiền nước qu&aacute; cảnh m&agrave; hiếm c&oacute; cơ hội được d&ugrave;ng ch&uacute;ng.<br />\r\n	<br />\r\n	Bạn cũng n&ecirc;n nhớ th&ecirc;m rằng, việc mua qu&aacute; nhiều h&agrave;ng h&oacute;a sẽ l&agrave;m cho h&agrave;nh l&yacute; x&aacute;ch tay của bạn cồng kềnh l&ecirc;n. L&uacute;c n&agrave;y bạn v&ocirc; t&igrave;nh đ&atilde; tạo th&ecirc;m sự bất tiện cho bản th&acirc;n. M&agrave; đối với v&agrave;i vật dụng dễ vỡ như c&aacute;c chai rượu, mắt k&iacute;nh th&igrave; lại c&agrave;ng phức tạp v&agrave; kh&oacute; khăn trong việc di chuyển.</p>\r\n<p>\r\n	<strong>6. Đề ph&ograve;ng kẻ gian:</strong><br />\r\n	<br />\r\n	Tuy s&acirc;n bay an ninh lu&ocirc;n được thắt chặt, thế nhưng bạn cũng đừng chủ quan rằng nơi đ&acirc;y kh&ocirc;ng c&oacute; trộm cắp xảy ra. Bạn phải lu&ocirc;n cảnh gi&aacute;c đề ph&ograve;ng. Bạn kh&ocirc;ng n&ecirc;n để qu&aacute; nhiều tiền mặt trong v&iacute; m&agrave; n&ecirc;n l&agrave;m một c&aacute;i t&uacute;i nhỏ v&agrave; đeo v&agrave;o cổ sẽ an to&agrave;n hơn.<br />\r\n	<br />\r\n	Ngo&agrave;i ra, bạn phải lu&ocirc;n bảo vệ h&agrave;nh l&yacute; x&aacute;ch tay của m&igrave;nh v&igrave; đ&ocirc;i khi bạn ngủ gật trong l&uacute;c ngồi chờ chuyến bay kế tiếp, kẻ gian sẽ lợi dụng đ&aacute;nh cắp t&uacute;i x&aacute;ch của bạn. Đặc biệt, nếu bạn c&oacute; mang theo laptop hay điện thoại di động th&igrave; lại c&agrave;ng phải n&ecirc;n cẩn thận về vấn đề n&agrave;y.<br />\r\n	<br />\r\n	Lưu &yacute; một v&agrave;i điểm nhỏ nhặt sẽ l&agrave;m cho việc qu&aacute; cảnh của bạn trở n&ecirc;n thật nhẹ nh&agrave;ng. Bạn sẽ tiết kiệm được nhiều sức lực trong những chuyến bay d&agrave;i m&agrave; đ&ocirc;i khi bạn lại c&ograve;n được tham quan một &iacute;t về đất nước nơi bạn qu&aacute; cảnh. Hy vọng những th&ocirc;ng tin tr&ecirc;n đ&acirc;y phần n&agrave;o sẽ gi&uacute;p cho c&aacute;c bạn giảm đi một phần lo lắng khi qu&aacute; cảnh.</p>', '#', '0');
INSERT INTO `info_detail` VALUES ('4', '3', 'Bệnh gì không được phép đi máy bay ?', '<p>\r\n	Những bệnh v&agrave; c&aacute;c điều kiện đi hoặc kh&ocirc;ng đi được m&aacute;y bay:<br />\r\n	Việc quyết định c&oacute; n&ecirc;n cho một bệnh nh&acirc;n đi m&aacute;y bay hay kh&ocirc;ng, kh&ocirc;ng những phụ thuộc v&agrave;o bản chất, mức độ nặng nhẹ của bệnh m&agrave; c&ograve;n phụ thuộc v&agrave;o c&aacute;c yếu tố: thời gian bay, độ cao bay, chịu &aacute;p suất thế n&agrave;o, việc cung cấp &ocirc;xy v&agrave; c&aacute;c thiết bị y học đầy đủ đến đ&acirc;u, c&oacute; thầy thuốc v&agrave; điều dưỡng vi&ecirc;n đi c&ugrave;ng hay kh&ocirc;ng v&agrave; c&aacute;c vấn đề lưu &yacute; đặc biệt kh&aacute;c. Mặc d&ugrave; h&agrave;ng kh&ocirc;ng của c&aacute;c quốc gia kh&ocirc;ng cho ph&eacute;p sử dụng c&aacute;c b&igrave;nh &ocirc;xy c&aacute; nh&acirc;n, nhưng tr&ecirc;n c&aacute;c chuyến bay người ta sẽ cung cấp &ocirc;xy theo y&ecirc;u cầu của thầy thuốc, của h&agrave;nh kh&aacute;ch. Sự cố y học hay gặp nhất tr&ecirc;n m&aacute;y bay l&agrave; thiếu &ocirc;xy v&agrave; c&aacute;c cấp cứu về tim mạch, ngất, t&acirc;m thần kinh...</p>\r\n<p>\r\n	<strong>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bệnh tim mạch:</strong></p>\r\n<p>\r\n	<br />\r\n	C&aacute;c trường hợp bệnh nh&acirc;n tim mạch kh&ocirc;ng được đi m&aacute;y bay gồm: bệnh tim mất b&ugrave;; suy tim sung huyết; bệnh động mạch v&agrave;nh c&oacute; tăng huyết &aacute;p nặng, tăng huyết &aacute;p m&agrave; chưa được kiểm so&aacute;t tốt; bệnh nh&acirc;n c&oacute; cơn đau thắt ngực mới ph&aacute;t nặng hoặc chưa ổn định; bệnh nh&acirc;n bị huyết khối tĩnh mạch s&acirc;u.<br />\r\n	<br />\r\n	Những trường hợp v&agrave; điều kiện c&oacute; thể đi m&aacute;y bay: bệnh tim mất b&ugrave; nhưng đ&atilde; điều trị hợp l&yacute;, tim đ&atilde; được b&ugrave;; hoặc bệnh nh&acirc;n được đi tr&ecirc;n m&aacute;y bay trang bị phương tiện tạo &aacute;p suất &ocirc;xy 100% suốt chuyến bay; bệnh nh&acirc;n nhồi m&aacute;u cơ tim cấp, đ&atilde; phục hồi v&agrave; kh&ocirc;ng c&oacute; triệu chứng từ 6 tuần trở l&ecirc;n; c&aacute;c trường hợp đau thắt ngực nhẹ hay vừa c&oacute; thể cho đi m&aacute;y bay, nhất l&agrave; m&aacute;y bay c&oacute; thiết bị tạo &aacute;p suất v&agrave; c&oacute; &ocirc;xy; c&aacute;c bệnh van tim hoặc tim c&ograve;n b&ugrave; kh&aacute;c: bệnh nh&acirc;n chỉ được ph&eacute;p bay ở độ cao từ 2.400-2.800m.<br />\r\n	<br />\r\n	Nếu muốn bay ở độ cao cao hơn th&igrave; m&aacute;y bay phải được trang bị phương tiện tạo &aacute;p suất v&agrave; cho bệnh nh&acirc;n thở &ocirc;xy từ độ cao 2.400m trở l&ecirc;n; bệnh nh&acirc;n huyết khối tĩnh mạch s&acirc;u cho đến khi liệu ph&aacute;p chống đ&ocirc;ng m&aacute;u đ&atilde; ổn định v&agrave; kh&ocirc;ng c&oacute; c&aacute;c biến chứng phổi. Lưu &yacute; rằng c&aacute;c chuyến bay đường d&agrave;i l&agrave;m tăng nguy cơ huyết khối tĩnh mạch s&acirc;u v&agrave; g&acirc;y bệnh nghẽn mạch; v&igrave; vậy để ph&ograve;ng tr&aacute;nh, bệnh nh&acirc;n cần phải ki&ecirc;ng rượu v&agrave; thuốc l&aacute;, đồng thời d&ugrave;ng aspirin liều thấp, sử dụng b&iacute;t tất hỗ trợ, vận động chi dưới v&agrave; đi bộ trong khi bay.</p>\r\n<p>\r\n	<strong>2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bệnh h&ocirc; hấp:</strong><br />\r\n	<br />\r\n	Những trường hợp kh&ocirc;ng n&ecirc;n đi m&aacute;y bay: bệnh nh&acirc;n đang l&ecirc;n cơn hen; ở t&igrave;nh trạng hen nặng; bệnh nh&acirc;n c&oacute; nang phổi bẩm sinh; bệnh nh&acirc;n bị lao đang hoạt động, l&acirc;y nhiễm hoặc tr&agrave;n kh&iacute; m&agrave;ng phổi; kh&oacute; thở khi nghỉ ngơi bị cấm bay; mức độ thiếu &ocirc;xy m&aacute;u v&agrave; tăng anhydrid carbonic nặng...<br />\r\n	<br />\r\n	C&aacute;c trường hợp bệnh h&ocirc; hấp c&oacute; thể đi m&aacute;y bay l&agrave;: dị ứng mũi v&agrave; nhiễm khuẩn nhưng cần lưu &yacute; l&agrave; sau khi bay dễ ph&aacute;t triển bệnh vi&ecirc;m tai do &aacute;p lực kh&iacute;. Để ngăn ngừa vi&ecirc;m tai do &aacute;p lực kh&iacute; (barotitis), trong khi bay bệnh nh&acirc;n n&ecirc;n nhai kẹo cao su, d&ugrave;ng thuốc chống ngạt mũi trước khi khởi h&agrave;nh 30 ph&uacute;t, d&ugrave;ng kh&aacute;ng sinh chống nhiễm khuẩn th&iacute;ch hợp v&agrave; tr&aacute;nh ngủ l&uacute;c m&aacute;y bay hạ xuống; bệnh nh&acirc;n hen nhẹ c&oacute; thể đi m&aacute;y bay b&igrave;nh thường.<br />\r\n	<br />\r\n	<strong>3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Phụ nữ c&oacute; thai:</strong></p>\r\n<p>\r\n	Thai phụ mang thai trong 8 th&aacute;ng đầu c&oacute; thể đi m&aacute;y bay b&igrave;nh thường ngoại trừ người c&oacute; tiền sử sảy thai hay đẻ non li&ecirc;n tiếp. Người c&oacute; thai th&aacute;ng thứ 9 kh&ocirc;ng n&ecirc;n cho đi m&aacute;y bay, nếu cần đi th&igrave; phải c&oacute; sự cho ph&eacute;p của thầy thuốc. Trẻ nhỏ dưới một tuần tuổi kh&ocirc;ng n&ecirc;n cho bay l&ecirc;n độ cao qu&aacute; hoặc bay đường d&agrave;i.<br />\r\n	<br />\r\n	<strong>4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bệnh thiếu m&aacute;u:</strong></p>\r\n<p>\r\n	Trường hợp thiếu m&aacute;u nặng, hemoglobin dưới 8,5g/dl hoặc số lượng hồng cầu dưới 3 triệu/ml kh&ocirc;ng được đi m&aacute;y bay cho đến khi bệnh nh&acirc;n được điều trị, hemoglobin đ&atilde; tăng đến mức hợp l&yacute;. Nếu hemoglobin dưới 8,5 - 9g/dl th&igrave; cần chuẩn bị sẵn &ocirc;xy. Đối với bệnh nh&acirc;n bị bệnh tế b&agrave;o h&igrave;nh liềm rất dễ bị rối loạn khi đi m&aacute;y bay n&ecirc;n cần phải hạn chế độ cao v&agrave; qu&atilde;ng đường bay.<br />\r\n	<br />\r\n	<strong>5.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bệnh đ&aacute;i th&aacute;o đường:</strong></p>\r\n<p>\r\n	Bệnh nh&acirc;n đ&aacute;i th&aacute;o đường m&agrave; kh&ocirc;ng cần d&ugrave;ng insulin hoặc c&oacute; thể chỉ d&ugrave;ng insulin mang theo m&igrave;nh trong chuyến bay th&igrave; c&oacute; thể bay an to&agrave;n. Những bệnh nh&acirc;n đ&aacute;i th&aacute;o đường &quot;dao động&quot; m&agrave; đang chịu những đợt hạ đường huyết th&igrave; cần được kiểm so&aacute;t tốt trước khi bay. Bệnh nh&acirc;n n&ecirc;n mang đường hay kẹo theo d&ugrave;ng trong khi bay để ph&ograve;ng phản ứng hạ đường huyết. Trong trường hợp phải bay đường d&agrave;i, việc điều chỉnh thời gian d&ugrave;ng insulin trước khi bay qua c&aacute;c m&uacute;i giờ cần được thầy thuốc hướng dẫn.<br />\r\n	<br />\r\n	<strong>6.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bệnh nh&acirc;n sau phẫu thuật:</strong></p>\r\n<p>\r\n	Những trường hợp sau phẫu thuật hoặc sau chấn thương mắt phải đi tr&ecirc;n c&aacute;c m&aacute;y bay c&oacute; cabin &aacute;p lực v&agrave; liệu ph&aacute;p &ocirc;xy để tr&aacute;nh tổn thương v&otilde;ng mạc do thiếu &ocirc;xy v&agrave; bọt kh&iacute; trong mắt. Bệnh nh&acirc;n mới mổ kh&ocirc;ng n&ecirc;n đi m&aacute;y bay trong 10 ng&agrave;y sau phẫu thuật bụng v&agrave; 21 ng&agrave;y sau phẫu thuật ngực; sau đ&oacute; chỉ bay khi đ&atilde; l&agrave;nh vết mổ v&agrave; kh&ocirc;ng phải dẫn lưu. Bệnh nh&acirc;n phải mở th&ocirc;ng đại tr&agrave;ng c&oacute; thể đi m&aacute;y bay miễn l&agrave; kh&ocirc;ng c&oacute; m&ugrave;i v&agrave; c&aacute;c t&uacute;i mở th&ocirc;ng đại tr&agrave;ng đ&atilde; thay mới trước khi bay. Bệnh nh&acirc;n tho&aacute;t vị bẹn hay tho&aacute;t vị đ&ugrave;i to, kh&ocirc;ng đỡ bằng băng treo hoặc b&oacute; th&igrave; kh&ocirc;ng cho đi m&aacute;y bay nếu m&aacute;y bay kh&ocirc;ng c&oacute; thiết bị tăng &aacute;p lực v&igrave; tăng nguy hiểm nghẹt ruột tho&aacute;t vị.<br />\r\n	<br />\r\n	<strong>7.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bệnh rối loạn t&acirc;m thần:</strong></p>\r\n<p>\r\n	Bệnh nh&acirc;n loạn thần nặng, hoặc trong trạng th&aacute;i k&iacute;ch động, loạn tr&iacute; kh&ocirc;ng được đi m&aacute;y bay kể cả khi c&oacute; thầy thuốc đi k&egrave;m. Bệnh nh&acirc;n hoảng loạn hay sợ h&atilde;i c&oacute; thể cho đi m&aacute;y bay nhưng phải uống thuốc an thần trấn tĩnh trước v&agrave; trong khi bay.<br />\r\n	<br />\r\n	<strong>8.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Người say t&agrave;u xe:</strong></p>\r\n<p>\r\n	Những người dễ bị say t&agrave;u xe cần phải uống thuốc chống say trước khi bay 30 ph&uacute;t v&agrave; trong l&uacute;c bay. Ăn nhẹ v&agrave; ăn thức ăn dễ ti&ecirc;u trước v&agrave; trong khi bay c&oacute; thể giảm triệu chứng say, giảm buồn n&ocirc;n v&agrave; n&ocirc;n. Đ&ecirc;m trước khi bay phải ngủ đẫy giấc, chuẩn bị sức khỏe tối ưu trước khi bay cũng c&oacute; t&aacute;c dụng tốt chống say t&agrave;u xe.</p>', '#', '0');
INSERT INTO `info_detail` VALUES ('5', '2', '10 điều “kỵ” khi đi máy bay', '<p>\r\n	<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Cởi gi&agrave;y đi ch&acirc;n đất</strong><strong>:</strong></p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Sau một chặng đường d&agrave;i, điều đầu ti&ecirc;n bạn muốn l&agrave;m khi ngồi xuống ghế l&agrave; khiến cho m&igrave;nh thật thoải m&aacute;i. Tất nhi&ecirc;n, bạn c&oacute; thể thoải m&aacute;i cởi gi&agrave;y để cho ch&acirc;n bạn được &ldquo;thở&rdquo; đ&ocirc;i ch&uacute;t.</p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Tuy nhi&ecirc;n, nếu ch&acirc;n bạn hơi &ldquo;nặng m&ugrave;i&rdquo; th&igrave; tốt nhất l&agrave; bạn kh&ocirc;ng n&ecirc;n cởi gi&agrave;y, hoặc &iacute;t ra l&agrave; vẫn để lại b&iacute;t tất. Ở nơi c&oacute; kh&ocirc;ng gian k&iacute;n như tr&ecirc;n m&aacute;y bay, m&ugrave;i từ ch&acirc;n bạn rất dễ bay ra xung quanh, ảnh hưởng đến những người kh&aacute;c, đặc biệt l&agrave; người ngồi ngay kế b&ecirc;n bạn.</p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Một số h&atilde;ng h&agrave;ng kh&ocirc;ng thậm ch&iacute; c&ograve;n c&oacute; dịch vụ cung cấp cho h&agrave;nh kh&aacute;ch tất ch&acirc;n để những ai c&oacute; nhu cầu cởi gi&agrave;y c&oacute; thể đeo loại tất n&agrave;y v&agrave;o v&agrave; vứt đ&ocirc;i tất bẩn đi.</p>\r\n<p style=\"margin-left: 0.5in;\">\r\n	<strong>2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Lạm dụng việc ngả ghế tựa:</strong></p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Những chiếc ghế tr&ecirc;n m&aacute;y bay được thiết kế c&oacute; thể ngả ra sau để khiến h&agrave;nh kh&aacute;ch cảm thấy thoải m&aacute;i hơn, nhưng bạn n&ecirc;n hạn chế ngả ghế v&agrave;o khoảng thời gian tiếp vi&ecirc;n phục vụ đồ ăn.</p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Người ngồi sau bạn chắc chắn chắn sẽ cảm thấy v&ocirc; c&ugrave;ng bất tiện khi chiếc ghế của bạn kề ngay trước mặt khi họ đang muốn ăn một ch&uacute;t g&igrave; đ&oacute;.</p>\r\n<p style=\"margin-left: 0.5in;\">\r\n	<strong>3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Chiếm qu&aacute; nhiều chỗ ở ngăn để t&uacute;i x&aacute;ch tay:</strong></p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Ngăn để h&agrave;nh l&yacute; tr&ecirc;n đầu chỉ đủ cho ch&uacute;ng ta để t&uacute;i x&aacute;ch tay. V&igrave; vậy, đừng chiếm th&ecirc;m chỗ bằng c&aacute;ch ch&egrave;n chiếc &aacute;o kho&aacute;c d&agrave;y khụ của bạn v&agrave;o đ&oacute;.</p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Tr&ecirc;n m&aacute;y bay thường c&oacute; một gi&aacute; g&aacute;c d&agrave;nh ri&ecirc;ng cho &aacute;o kho&aacute;c, h&atilde;y nhờ tiếp vi&ecirc;n h&agrave;ng kh&ocirc;ng treo gi&uacute;p bạn. Nếu ai đ&oacute; y&ecirc;u cầu bạn dịch h&agrave;nh l&yacute; gọn hơn cho họ để t&uacute;i nghĩa l&agrave; bạn đang chiếm qu&aacute; nhiều chỗ đấy.</p>\r\n<p style=\"margin-left: 0.5in;\">\r\n	<strong>4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>N&oacute;i chuyện qu&aacute; to:</strong></p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Một số người rất th&iacute;ch chuyện tr&ograve; v&agrave; kết bạn với người lạ, điều đ&oacute; kh&ocirc;ng c&oacute; g&igrave; l&agrave; sai cả. Nhưng khi t&acirc;m t&igrave;nh với người bạn mới, bạn h&atilde;y cố gắng kiểm so&aacute;t &acirc;m lượng của tiếng chuyện tr&ograve; v&agrave; cười n&oacute;i. C&aacute;c h&agrave;nh kh&aacute;ch kh&aacute;c tr&ecirc;n m&aacute;y bay thực sự kh&ocirc;ng quan t&acirc;m việc bạn đến từ đ&acirc;u v&agrave; l&agrave;m g&igrave; để kiếm sống đ&acirc;u.</p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Nếu người ngồi gần bạn r&uacute;t ra một quyển s&aacute;ch hay tạp ch&iacute; th&igrave; đ&oacute; l&agrave; dấu hiệu cho thấy họ muốn dừng c&acirc;u chuyện đấy.</p>\r\n<p style=\"margin-left: 0.5in;\">\r\n	<strong>5.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Đ&ugrave;a qu&aacute; lố:</strong></p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Đừng đưa ra những tuy&ecirc;n bố g&acirc;y shock như &ldquo;T&ocirc;i c&oacute; một quả bom&rdquo;, hoặc l&agrave;m những tr&ograve; đ&ugrave;a ngu ngốc tương tự như vậy. Bạn c&oacute; thể bị &ldquo;n&eacute;m&rdquo; khỏi m&aacute;y bay v&agrave; thậm ch&iacute; l&agrave; đưa v&agrave;o nh&agrave; đ&aacute; chỉ v&igrave; ph&uacute;t nghịch ngợm trẻ con của m&igrave;nh.</p>\r\n<p style=\"margin-left: 0.5in;\">\r\n	<strong>6.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Nh&igrave;n chằm chằm v&agrave;o tiếp vi&ecirc;n từ đầu tới ch&acirc;n:</strong></p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	C&aacute;c tiếp vi&ecirc;n h&agrave;ng kh&ocirc;ng nổi tiếng l&agrave; c&oacute; ngoại h&igrave;nh v&agrave; cử chỉ đẹp, thu h&uacute;t mọi &aacute;nh nh&igrave;n. Nhưng bạn kh&ocirc;ng n&ecirc;n nh&igrave;n chằm chằm từ tr&ecirc;n xuống dưới mỗi khi nữ tiếp vi&ecirc;n đi qua.</p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Kh&ocirc;ng chỉ tiếp vi&ecirc;n đ&oacute; sẽ cảm thấy kh&ocirc;ng thoải m&aacute;i m&agrave; c&aacute;c h&agrave;nh kh&aacute;ch kh&aacute;c cũng sẽ nh&igrave;n bạn như người ngo&agrave;i h&agrave;nh tinh.</p>\r\n<p style=\"margin-left: 0.5in;\">\r\n	<strong>7.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>D&iacute;nh b&atilde; kẹo cao su lung tung:</strong></p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Bạn h&atilde;y tưởng tượng khi m&igrave;nh nhấc chiếc khay ăn l&ecirc;n th&igrave; một mẩu b&atilde; kẹo cao su đập ngay v&agrave;o mắt bạn. Chắc chắn l&agrave; kh&ocirc;ng ai cảm thấy vui mừng khi gặp phải t&igrave;nh huống n&agrave;y. V&igrave; vậy, nhai kẹo cao su xong, bạn h&atilde;y g&oacute;i b&atilde; kẹo v&agrave;o giấy g&oacute;i v&agrave; vứt ở nơi quy định.</p>\r\n<p style=\"margin-left: 0.5in;\">\r\n	<strong>8.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Để &acirc;m lượng headphone qu&aacute; to:</strong></p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	D&ugrave; l&agrave; bạn đ&atilde; nghe headphone, nhưng nếu thể loại y&ecirc;u th&iacute;ch của bạn l&agrave; nhạc rock th&igrave; n&oacute; vẫn g&acirc;y ồn &agrave;o cho người b&ecirc;n cạnh. H&atilde;y để &acirc;m lượng vừa đủ nghe cho m&igrave;nh bạn th&ocirc;i để người b&ecirc;n cạnh được nghỉ ngơi trong suốt chuyến bay d&agrave;i.</p>\r\n<p style=\"margin-left: 0.5in;\">\r\n	<strong>9.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Tỏ ra qu&aacute; th&acirc;n mật tr&ecirc;n m&aacute;y bay:</strong></p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Một c&aacute;i thơm nhẹ l&ecirc;n m&aacute; l&agrave; ho&agrave;n to&agrave;n b&igrave;nh thường, nhưng những cử chỉ th&acirc;n mật nhạy cảm hơn th&igrave; n&ecirc;n được &ldquo;để d&agrave;nh&rdquo; đến kh&aacute;ch sạn.</p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Những h&agrave;nh động &ldquo;l&eacute;n l&uacute;t&rdquo; dưới chăn tr&ecirc;n m&aacute;y bay cũng n&ecirc;n tr&aacute;nh. Mọi người c&oacute; thể kh&ocirc;ng muốn th&ocirc; lỗ nhắc nhở bạn, nhưng họ sẽ cảm thấy thực sự kh&ocirc;ng thoải m&aacute;i.</p>\r\n<p style=\"margin-left: 0.5in;\">\r\n	<strong>10.&nbsp; </strong><strong>Đ&aacute; v&agrave;o ghế trước:</strong></p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Một số người thường v&ocirc; &yacute; l&agrave;m h&agrave;nh động n&agrave;y khi cố gắng x&ecirc; dịch để t&igrave;m được tư thế ngủ thoải m&aacute;i. Nhất l&agrave; những người n&agrave;o cao lớn, ch&acirc;n d&agrave;i th&igrave; thực sự l&agrave; kh&oacute; t&igrave;m được chỗ để ch&acirc;n.</p>\r\n<p style=\"margin-left: 0.25in;\">\r\n	Tuy nhi&ecirc;n, nếu điều n&agrave;y diễn ra li&ecirc;n tục, người ngồi ph&iacute;a trước bạn sẽ cảm thấy thực sự bực m&igrave;nh. Trong trường hợp bị nhắc nhở, bạn h&atilde;y ch&uacute; &yacute; cẩn thận hơn.</p>', '#', '0');
INSERT INTO `info_detail` VALUES ('6', '3', 'Thủ tục đi máy bay với người đi lần đầu', '<p>\r\n	1. Thủ tục đi&nbsp;<a href=\"http://phongvegiare.vn/\" target=\"_blank\" title=\"máy bay\">m&aacute;y bay</a>:</p>\r\n<p>\r\n	<em>1.1.</em><em>Chuẩn bị:</em></p>\r\n<p>\r\n	Trước khi đi m&aacute;y bay bạn phải c&oacute; trong tay:</p>\r\n<p>\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<a href=\"http://phongvegiare.vn/\" target=\"_blank\" title=\"vé máy bay\">v&eacute; m&aacute;y bay</a></p>\r\n<p>\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Chứng minh thư nếu đi trong nước hoặc hộ chiếu nếu đi nước ngo&agrave;i.</p>\r\n<p>\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Visa nếu nước cần đến y&ecirc;u cầu. Hiện nay một số nước v&agrave; Việt Nam đ&atilde; k&yacute; hiệp định miễn visa đối với c&ocirc;ng d&acirc;n của hai nước mang hộ chiếu phổ th&ocirc;ng nếu ở lại trong v&ograve;ng 30 ng&agrave;y. V&iacute; dụ,&nbsp; Th&aacute;i Lan, Indonesia, Singapore &hellip; như vậy khi đến c&aacute;c nước n&agrave;y trong v&ograve;ng 30 ng&agrave;y bạn kh&ocirc;ng cần visa. Một số nước y&ecirc;u cầu phải c&oacute; Visa Transit nếu tuyến bay của bạn transit qua s&acirc;n bay tại nước đ&oacute;, v&iacute; dụ bạn bay từ H&agrave; Nội đến Amsterdam (H&agrave; Lan) transit tại s&acirc;n bay Heathrow ở London (Anh): HAN &ndash; LHR &ndash; AMS, bạn phải c&oacute; th&ecirc;m visa transit tại Anh b&ecirc;n cạnh visa v&agrave;o H&agrave; Lan.</p>\r\n<p>\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Tiền mặt để đ&oacute;ng thuế s&acirc;n bay khi đi: một số s&acirc;n bay ch&acirc;u &Aacute; như Nội B&agrave;i (H&agrave; Nội), Don Muang (Bangkok)&hellip; thu lệ ph&iacute; s&acirc;n bay khi bạn đi m&aacute;y bay từ nơi đ&oacute; (trong khi c&aacute;c s&acirc;n bay ở nơi kh&aacute;c gộp thuế đ&oacute; v&agrave;o lu&ocirc;n gi&aacute;&nbsp;<a href=\"http://phongvegiare.vn/\" target=\"_blank\" title=\"vé máy bay\">v&eacute; m&aacute;y bay</a>), th&ocirc;ng thường lệ ph&iacute; n&agrave;y thu bằng đơn vị tiền của nước sở tại, để tr&aacute;nh phải đổi tiền tại s&acirc;n bay (nơi m&agrave; tỉ gi&aacute; thường thấp) bạn n&ecirc;n chuẩn bị sẵn số tiền n&agrave;y. Ngo&agrave;i ra, bạn cũng n&ecirc;n c&oacute; trước một &iacute;t tiền của nước đến để c&oacute; thể ti&ecirc;u ngay m&agrave; kh&ocirc;ng cần phải đổi ở s&acirc;n bay.</p>\r\n<p>\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B&ecirc;n cạnh đ&oacute; l&agrave; c&aacute;c đồ d&ugrave;ng m&agrave; bạn muốn mang theo, chia l&agrave;m hai loại:</p>\r\n<p>\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &bull; Đồ x&aacute;ch tay (hand/cabin luggage) l&agrave; đồ m&agrave; bạn mang theo b&ecirc;n m&igrave;nh khi l&ecirc;n m&aacute;y bay, bạn sẽ để đồ n&agrave;y l&ecirc;n gi&aacute; ph&iacute;a tr&ecirc;n ghế ngồi hoặc dưới gầm ghế hoặc giữ b&ecirc;n m&igrave;nh. H&agrave;nh l&yacute; dạng n&agrave;y thường c&oacute; giới hạn về k&iacute;ch thước, c&acirc;n nặng, số lượng t&uacute;i được mangtheo &hellip; t&ugrave;y theo quyđịnh của từng&nbsp;<a href=\"http://phongvegiare.vn/\" target=\"_blank\" title=\"Hãng Hàng không\">H&atilde;ng H&agrave;ng kh&ocirc;ng</a>. Do vấn đề về an ninh, bạn kh&ocirc;ng được ph&eacute;p mang theo c&aacute;c vật dụng c&oacute; &yacute; s&aacute;t thương như dao, k&eacute;o&nbsp;</p>\r\n<p>\r\n	&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đồ gửi (checked luggage) thường l&agrave; đồ nặng, cồng kềnh m&agrave; bạn gửi khi l&agrave;m thủ tục check-in, đồ n&agrave;y vẫn đi c&ugrave;ng chuyến bay với bạn nhưng được để ở khoang h&agrave;nh l&yacute; ri&ecirc;ng, bạn chỉ c&oacute; thể lấy đồ khi đến nơi. T&ugrave;y theo loại&nbsp;<a href=\"http://phongvegiare.vn/\" target=\"_blank\" title=\"vé máy bay\">v&eacute; m&aacute;y bay</a>,&nbsp;<a href=\"http://phongvegiare.vn/\" target=\"_blank\" title=\"Hãng Hàng không\">H&atilde;ng H&agrave;ng kh&ocirc;ng</a>m&agrave; số lượng đồ gửi (t&iacute;nh theo kiện/bao/g&oacute;i) hay c&acirc;n nặng được quy định kh&aacute;c nhau, nếu bạn gửi qu&aacute; mức qui định th&igrave; phải đ&oacute;ng th&ecirc;m tiền. C&aacute;c h&atilde;ng đều qui định danh mục c&aacute;c mặt h&agrave;ng kh&ocirc;ng được ph&eacute;p mang theo, bạn phải tu&acirc;n thủ quy định n&agrave;y.</p>\r\n<p>\r\n	1.2.<a href=\"http://phongvegiare.vn/\" title=\"ve may bay\"><em>V&eacute; m&aacute;y bay</em></a>:</p>\r\n<p>\r\n	Bạn c&oacute; thể mua v&eacute; m&aacute;y bay trực tiếp qua c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng hoặc qua c&aacute;c đại l&yacute;, thường mua qua đại l&yacute; hay mua trước khi đi một thời gian th&igrave; gi&aacute; sẽ rẻ hơn. Một số&nbsp;<a href=\"http://phongvegiare.vn/\" target=\"_blank\" title=\"Hãng Hàng không\">H&atilde;ng H&agrave;ng kh&ocirc;ng</a>&nbsp;hiện nay b&aacute;n v&eacute; qua internet, do kh&ocirc;ng phải trả th&ecirc;m tiền thu&ecirc; nh&acirc;n vi&ecirc;n, địa điểm&hellip; n&ecirc;n gi&aacute; v&eacute; cũng ngang với gi&aacute; của đại l&yacute;. Ở ch&acirc;u &Acirc;u c&ograve;n c&oacute; c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng gi&aacute; rẻ (low cost Airline), những h&atilde;ng n&agrave;y thường c&oacute; c&aacute;c tuyến bay từ c&aacute;c th&agrave;nh phố lớn đến c&aacute;c điểm du lịch nổi tiếng ở ch&acirc;u &Acirc;u, chuyến bay thường k&eacute;o d&agrave;i khoảng 1 - 4 tiếng, kh&ocirc;ng phục vụ ăn miễn ph&iacute; n&ecirc;n gi&aacute; v&eacute; rất rẻ.</p>\r\n<p>\r\n	Đ&ocirc;i khi c&oacute; những đợt khuyến mại th&igrave; gi&aacute; v&eacute; cũng thấp hơn so với b&igrave;nh thường, đổi lại sẽ c&oacute; một số hạn chế v&iacute; dụ số kg đồ gửi thấp, thời gian hợp lệ của v&eacute; ngắn, kh&ocirc;ng được trả lại v&eacute; một khi đ&atilde; mua hoặc số tiền trả lại sẽ thấp so với v&eacute; kh&ocirc;ng khuyến mại &hellip;</p>\r\n<p>\r\n	<strong>C&aacute;c th&ocirc;ng tin li&ecirc;n quan đến v&eacute; m&aacute;y bay:</strong></p>\r\n<p>\r\n	&bull; Hạng v&eacute; (class): T&ugrave;y theo ph&acirc;n loại của từng h&atilde;ng m&agrave; c&oacute; thể c&oacute; nhiều hạng kh&aacute;c nhau, phổ biến c&oacute; Economy, Bussiness v&agrave; First class, trong đ&oacute; hạng Economy l&agrave; rẻ nhất.</p>\r\n<p>\r\n	&bull; T&igrave;nh trạng v&eacute; (status):</p>\r\n<p>\r\n	- OK/Confirmed: Ng&agrave;y giờ bay l&agrave; ch&iacute;nh thức.</p>\r\n<p>\r\n	- Wait Listed: Ng&agrave;y giờ bay m&agrave; bạn đ&atilde; đăng k&yacute; chưa được chắc chắn do đ&atilde; hết chỗ v&agrave; bạn được cho v&agrave;o danh s&aacute;ch đợi. Nếu c&oacute; người bỏ chỗ m&agrave; bạn đứng đầu danh s&aacute;ch đợi th&igrave; bạn sẽ được đi v&agrave;o chuyến đ&atilde; đăng k&yacute;, khi đ&oacute; status của bạn chuyển th&agrave;nh OK.</p>\r\n<p>\r\n	- Open Dated: bạn chưa đăng k&yacute; ng&agrave;y, giờ bay. Bạn c&oacute; thể đăng k&yacute; bất cứ l&uacute;c n&agrave;o miễn l&agrave; trước khi v&eacute; hết hạn sử dụng.</p>\r\n<p>\r\n	&bull; Chuyến bay (flight): K&yacute; hiệu chuyến bay, v&iacute; dụ VN188, bạn c&oacute; thể t&igrave;m th&ocirc;ng tin về chuyến bay tr&ecirc;n c&aacute;c bảng điện tử qua k&yacute; hiệu n&agrave;y.</p>\r\n<p>\r\n	&bull; Ng&agrave;y giờ bay (date/time): Ng&agrave;y/giờ m&aacute;y bay cất c&aacute;nh ở nơi đi v&agrave; hạ c&aacute;nh ở nơi đến, giờ địa phương.</p>\r\n<p>\r\n	&bull; Bay thẳng (non-stop) hay qu&aacute; cảnh (transit): Bay thẳng l&agrave; chuyến bay đi thẳng từ điểm bạn cần đi đến nơi bạn cần đến, transit l&agrave; chuyến bay dừng tại một hoặc v&agrave;i nơi kh&aacute;c trước khi đến điểm bạn cần đến.</p>\r\n<p>\r\n	<strong>C&aacute;c điều kiện r&agrave;ng buộc:</strong></p>\r\n<p>\r\n	&bull; Trọng lượng h&agrave;nh l&yacute; gửi: T&ugrave;y theo loại v&eacute;, h&atilde;ng h&agrave;ng kh&ocirc;ng m&agrave; số kg h&agrave;nh l&yacute; c&oacute; giới hạn kh&aacute;c nhau. Th&ocirc;ng thường đi đường d&agrave;i được gửi 30kg/người, đi nhiều người với nhau c&oacute; thể gộp lại để chung tải. Nếu gửi qu&aacute; mức qui định th&igrave; phải đ&oacute;ng th&ecirc;m tiền, lệ ph&iacute; cao.</p>\r\n<p>\r\n	&bull; Thời hạn hợp lệ của v&eacute;: T&iacute;nh từ l&uacute;c mua hoặc từ l&uacute;c bắt đầu bay, c&oacute; loại 1 th&aacute;ng, 3 th&aacute;ng, 6 th&aacute;ng, 1 năm&hellip; t&ugrave;y theo từng h&atilde;ng. Thời hạn c&agrave;ng d&agrave;i th&igrave; gi&aacute; c&agrave;ng cao.</p>\r\n<p>\r\n	&bull; Refundable: V&eacute; c&oacute; thể trả lại hay kh&ocirc;ng khi bạn kh&ocirc;ng thể tiếp tục chuyến bay, t&ugrave;y theo qui định m&agrave; bạn được trả lại một phần tiền hoặc kh&ocirc;ng.</p>\r\n<p>\r\n	<strong>Mua v&eacute;:</strong></p>\r\n<p>\r\n	&bull; Đặt chỗ: Bạn th&ocirc;ng b&aacute;o cho nơi b&aacute;n địa điểm đi v&agrave; đến, ng&agrave;y giờ bay nếu đ&atilde; x&aacute;c định, hạng v&eacute;, một chiều hay hai chiều. B&ecirc;n b&aacute;n v&eacute; sẽ cho bạn biết: chuyến bay tương ứng c&ograve;n chỗ hay kh&ocirc;ng, nếu hết chỗ của hạng v&eacute; đăng k&yacute; th&igrave; bạn c&oacute; muốn v&agrave;o danh s&aacute;ch đợi hay kh&ocirc;ng, gi&aacute; v&eacute;, c&aacute;c điều kiện r&agrave;ng buộc của v&eacute;, thời hạn phải trả tiền lấy v&eacute;. Hai b&ecirc;n thỏa thuận với nhau. Khi chưa trả tiền, bạn c&oacute; thể thay đổi lại c&aacute;c th&ocirc;ng tin đặt chỗ hoặc hủy bỏ m&agrave; kh&ocirc;ng phải trả lệ ph&iacute;.</p>\r\n<p>\r\n	&bull; Trả tiền, lấy v&eacute;: T&ugrave;y theo loại v&eacute;, qui định của từng đại l&yacute;, h&atilde;ng h&agrave;ng kh&ocirc;ng m&agrave; bạn sẽ phải trả th&ecirc;m tiền nếu muốn thay đổi lại c&aacute;c th&ocirc;ng tin đ&atilde; ghi tr&ecirc;n mặt v&eacute; một khi đ&atilde; nhận v&eacute;. Đối với loại v&eacute; chưa x&aacute;c định ng&agrave;y giờ bay (Open Dated), bạn c&oacute; thể trả tiền lấy v&eacute; rồi đặt chỗ sau m&agrave; kh&ocirc;ng phải trả lệ ph&iacute;. Tuy nhi&ecirc;n, nếu sau đ&oacute; bạn muốn thay đổi ng&agrave;y giờ bay th&igrave; việc c&oacute; &aacute;p dụng mức ph&iacute; hay kh&ocirc;ng lại t&ugrave;y thuộc v&agrave;o loại v&eacute; bạn đ&atilde; mua, đại l&yacute;, h&atilde;ng h&agrave;ng kh&ocirc;ng. C&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng đều c&oacute; địa chỉ li&ecirc;n lạc tại th&agrave;nh phố nơi bạn sẽ đi, bạn c&oacute; thể gọi đến địa chỉ n&agrave;y để li&ecirc;n lạc thay v&igrave; phải gọi về nơi đ&atilde; mua v&eacute;.</p>\r\n<p>\r\n	<strong><em>1.3. S&acirc;n bay<br />\r\n	</em></strong>C&aacute;c s&acirc;n bay thường nằm c&aacute;ch trung t&acirc;m th&agrave;nh phố từ v&agrave;i km đến v&agrave;i chục km, c&aacute;c th&agrave;nh phố ph&aacute;t triển thường c&oacute; c&aacute;c phương tiện giao th&ocirc;ng c&ocirc;ng cộng (bus, metro, tram, train) nối s&acirc;n bay với trung t&acirc;m, b&ecirc;n cạnh c&aacute;c phương tiện kh&aacute;c đắt tiền hơn như taxi, express bus.</p>\r\n<p>\r\n	S&acirc;n bay bao giờ cũng chia l&agrave;m 2 khu t&aacute;ch biệt:</p>\r\n<p>\r\n	&bull; Khu đến (arrival): Nơi m&agrave; h&agrave;nh kh&aacute;ch từ nơi kh&aacute;c bay đến sẽ đi ra ở khu vực n&agrave;y.</p>\r\n<p>\r\n	&bull; Khu đi (departure): Nơi m&agrave; h&agrave;nh kh&aacute;ch l&agrave;m thủ tục để đi m&aacute;y bay đến nơi kh&aacute;c.</p>\r\n<p>\r\n	Trong mỗi khu c&oacute; thể chia l&agrave;m hai: nội địa (d&agrave;nh cho c&aacute;c chuyến bay nội địa) v&agrave; quốc tế (d&agrave;nh cho c&aacute;c chuyến bay quốc tế).</p>\r\n<p>\r\n	Với c&aacute;c s&acirc;n bay lớn, từng khu n&oacute;i tr&ecirc;n c&ograve;n chia l&agrave;m bến (terminal) (v&iacute; dụ ở s&acirc;n bay Bangkok, Singapore), mỗi terminal c&oacute; một số h&atilde;ng h&agrave;ng kh&ocirc;ng nhất định hoạt động, bạn đi h&atilde;ng n&agrave;o hoặc đ&oacute;n người th&acirc;n đi h&atilde;ng n&agrave;o th&igrave; đến terminal tương ứng của khu tương ứng.</p>\r\n<p>\r\n	C&aacute;c biển b&aacute;o ở s&acirc;n bay đều ghi rất r&otilde; bằng hai thứ tiếng: Tiếng địa phương v&agrave; tiếng Anh. S&acirc;n bay lu&ocirc;n c&oacute; c&aacute;c trạm th&ocirc;ng tin (information point) để gi&uacute;p đỡ h&agrave;nh kh&aacute;ch. C&aacute;c s&acirc;n bay lớn thậm ch&iacute; c&ograve;n cho in sơ đồ s&acirc;n bay.</p>\r\n<p>\r\n	C&aacute;c cửa h&agrave;ng b&aacute;n đồ lưu niệm, đồ ăn, toilet đều c&oacute; ở s&acirc;n bay. Bạn n&ecirc;n mang đồ ăn từ nh&agrave; theo nếu muốn tiết kiệm, th&ocirc;ng thường khi đi m&aacute;y bay, bạn phải đến trước giờ cất c&aacute;nh 2 &ndash; 3 tiếng để l&agrave;m c&aacute;c thủ tục.</p>\r\n<p>\r\n	C&aacute;c bảng điện tử nằm rải r&aacute;c trong s&acirc;n bay th&ocirc;ng b&aacute;o c&aacute;c chuyến bay đến v&agrave; đi, bạn xem th&ocirc;ng tin ở đ&acirc;y v&agrave; để &yacute; loa th&ocirc;ng b&aacute;o. Cần ch&uacute; &yacute; để kh&ocirc;ng nhầm giữa th&ocirc;ng tin đi (departure) v&agrave; đến (arrival), c&aacute;c chuyến bay của terminal n&agrave;o.</p>\r\n<p>\r\n	Th&ocirc;ng tin về chuyến bay đi gồm c&oacute;: T&ecirc;n chuyến bay, điểm đi/đến, ng&agrave;y giờ đi/đến, t&igrave;nh trạng (chưa c&oacute; th&ocirc;ng tin, bắt đầu check-in, bắt đầu v&agrave;o ph&ograve;ng đợi, chuẩn bị cất c&aacute;nh &hellip;).&nbsp;<br />\r\n	Th&ocirc;ng tin về chuyến bay đến gồm c&oacute;: T&ecirc;n chuyến bay, điểm đi/đến, ng&agrave;y giờ đi/đến, t&igrave;nh trạng (chưa c&oacute; th&ocirc;ng tin, sắp đến, đ&atilde; đến &hellip;).</p>\r\n<p>\r\n	<strong><em>1.4. Check-in:<br />\r\n	</em></strong>Ở khu departure, bạn t&igrave;m đến c&aacute;c quầy của h&atilde;ng h&agrave;ng kh&ocirc;ng m&agrave; bạn đi, thường c&oacute; hai hoặc nhiều quầy. Nếu đ&atilde; đến giờ check-in, c&aacute;c quầy sẽ c&oacute; nh&acirc;n vi&ecirc;n phục vụ. Nếu v&eacute; của bạn chưa ghi chỗ ngồi (seat) m&agrave; bạn muốn chọn chỗ th&igrave; n&ecirc;n đến sớm để xếp h&agrave;ng. T&ugrave;y theo hạng v&eacute; m&agrave; bạn xếp h&agrave;ng v&agrave;o quầy tương ứng. Hạng Bussiness hay First class c&oacute; quầy ri&ecirc;ng, chỉ khi n&agrave;o kh&ocirc;ng c&oacute; kh&aacute;ch đi hạng n&agrave;y m&agrave; c&oacute; đ&ocirc;ng kh&aacute;ch đợi ở c&aacute;c hạng kh&aacute;c th&igrave; c&aacute;c nh&acirc;n vi&ecirc;n mới linh động l&agrave;m thủ tục cho kh&aacute;ch c&aacute;c hạng kh&aacute;c ở quầy n&agrave;y.</p>\r\n<p>\r\n	Trước khi đi v&agrave;o c&aacute;c quầy để l&agrave;m thủ tục, bạn c&oacute; thể phải đi qua trạm kiểm so&aacute;t đồ, bạn chỉ phải đưa h&agrave;nh l&yacute; gửi l&ecirc;n để scan, đồ x&aacute;ch tay sẽ kiểm tra ở c&aacute;c bước sau.</p>\r\n<p>\r\n	Bạn phải đưa v&eacute; m&aacute;y bay, chứng minh thư hoặc hộ chiếu/visa cho nh&acirc;n vi&ecirc;n tại quầy kiểm tra, nếu c&oacute; đồ gửi bạn đưa l&ecirc;n b&agrave;n c&acirc;n, nh&acirc;n vi&ecirc;n c&oacute; thể kiểm tra lu&ocirc;n h&agrave;nh l&yacute; x&aacute;ch tay của bạn c&oacute; hợp lệ kh&ocirc;ng. Nếu đi một nh&oacute;m, bạn c&oacute; thể l&agrave;m thủ tục c&ugrave;ng l&uacute;c với họ. Bạn c&oacute; thể đề nghị được ngồi cạnh cửa sổ hay cạnh lối đi, nếu c&ograve;n chỗ trong hạng tương ứng nh&acirc;n vi&ecirc;n sẽ xếp cho bạn theo y&ecirc;u cầu. Ngồi cạnh cửa sổ th&iacute;ch hợp với những chuyến bay ngắn, bạn kh&ocirc;ng bị quấy rầy bởi người ngồi ph&iacute;a trong khi họ muốn ra ngo&agrave;i, hoặc bạn c&oacute; thể ngắm cảnh khi m&aacute;y bay cất/hạ c&aacute;nh. Nếu bạn kh&ocirc;ng muốn l&agrave;m phiền người kh&aacute;c khi hay phải đi toilet th&igrave; bạn c&oacute; thể xin ngồi cạnh lối đi.</p>\r\n<p>\r\n	Sau khi xong thủ tục, bạn sẽ nhận lại v&eacute; m&aacute;y bay (đ&atilde; bị x&eacute; đi trang tương ứng với chuyến bay đang l&agrave;m thủ tục), giấy tờ đưa l&uacute;c trước, thẻ l&ecirc;n m&aacute;y bay (Boarding Pass) v&agrave; cuống v&eacute; tương ứng với h&agrave;nh l&yacute; gửi. C&oacute; bao nhi&ecirc;u kiện h&agrave;nh l&yacute; gửi th&igrave; c&oacute; bấy nhi&ecirc;u cuống v&eacute;, bạn phải giữ cuống v&eacute; cho đến khi ra khỏi s&acirc;n bay.</p>\r\n<p>\r\n	Từ l&uacute;c n&agrave;y đến l&uacute;c l&ecirc;n m&aacute;y bay, bạn sẽ d&ugrave;ng Boarding Pass thay thế cho v&eacute; m&aacute;y bay. Tr&ecirc;n thẻ n&agrave;y ghi địa chỉ ph&ograve;ng đợi tức l&agrave; cổng (gate) m&agrave; bạn sẽ phải c&oacute; mặt trước khi l&ecirc;n m&aacute;y bay, thời gian ph&ograve;ng đợi mở cửa, số ghế (seat) tr&ecirc;n m&aacute;y bay của bạn.</p>\r\n<p>\r\n	Trong trường hợp bạn đi transit, nh&acirc;n vi&ecirc;n c&oacute; thể sẽ l&agrave;m lu&ocirc;n Boarding Pass cho (c&aacute;c) chặng tiếp theo của bạn, bạn ch&uacute; &yacute; kh&ocirc;ng nhầm giữa c&aacute;c Boarding Pass với nhau. T&ugrave;y theo t&igrave;nh h&igrave;nh họ sẽ th&ocirc;ng b&aacute;o cho bạn biết h&agrave;nh l&yacute; gửi của bạn sẽ được tự động chuyển giữa c&aacute;c chuyến bay v&agrave; bạn sẽ lấy đồ ở đ&iacute;ch cuối c&ugrave;ng hay phải tự lo ở từng địa điểm.</p>\r\n<p>\r\n	Sau khi l&agrave;m thủ tục check-in xong bạn c&oacute; thể đi thẳng v&agrave;o c&aacute;c khu vực b&ecirc;n trong hoặc chưa v&agrave;o ngay nếu c&ograve;n nhiều thời gian. Lưu &yacute;, bạn c&oacute; thể bị tắc trong c&aacute;c h&agrave;ng đợi ở c&aacute;c bước l&agrave;m thủ tục xuất cảnh hay kiểm tra an ninh.</p>\r\n<p>\r\n	Nếu s&acirc;n bay thu lệ ph&iacute;, bạn n&ecirc;n mua ngay để tr&aacute;nh mất thời gian sau n&agrave;y.</p>\r\n<p>\r\n	<strong><em>1.5.&nbsp;</em></strong><a href=\"http://phongvegiare.vn/\" title=\"lam thu tuc xuat canh\"><strong><em>L&agrave;m thủ tục xuất cảnh</em></strong></a><br />\r\n	T&ugrave;y theo s&acirc;n bay, bước kiểm tra an ninh c&oacute; thể diễn ra trước hoặc sau bước l&agrave;m thủ tục xuất cảnh.</p>\r\n<p>\r\n	Nếu bạn đi ra nước ngo&agrave;i, bạn sẽ phải l&agrave;m thủ tục xuất cảnh. Tại đ&acirc;y, bạn đưa ra hộ chiếu/visa, nh&acirc;n vi&ecirc;n hải quan xem x&eacute;t v&agrave; c&oacute; thể đ&oacute;ng dấu v&agrave;o hộ chiếu của bạn hay gỡ bỏ visa/giấy tờ li&ecirc;n quan khỏi hộ chiếu của bạn nếu đ&atilde; hết gi&aacute; trị.<br />\r\n	<strong><em>1.6. Kiểm tra an ninh:</em></strong>Đồ x&aacute;ch tay của bạn sẽ được đưa qua m&aacute;y qu&eacute;t, v&agrave; bạn sẽ đi qua một cổng từ, ch&igrave;a kh&oacute;a hay c&aacute;c đồ kim loại bạn n&ecirc;n cho ra khay để qua đường m&aacute;y qu&eacute;t, tr&aacute;nh l&agrave;m mất thời gian của cả hai b&ecirc;n một c&aacute;ch kh&ocirc;ng cần thiết.<br />\r\n	&nbsp;<strong>1.7. V&agrave;o ph&ograve;ng đợi:</strong></p>\r\n<p>\r\n	Sau khi xong hai bước tr&ecirc;n l&agrave; bạn đ&atilde; v&agrave;o khu vực quốc tế, tại đ&acirc;y c&oacute; c&aacute;c quầy h&agrave;ng lưu niệm, đồ ăn&hellip; bạn c&oacute; thể đi dạo nếu c&ograve;n thời gian hoặc đến thẳng ph&ograve;ng đợi. C&aacute;c s&acirc;n bay lớn thường c&oacute; c&aacute;c khu giải tr&iacute; gi&uacute;p h&agrave;nh kh&aacute;ch ti&ecirc;u thời gian nếu phải chờ đợi l&acirc;u (như khi đi transit).</p>\r\n<p>\r\n	Bạn c&oacute; thể y&ecirc;u cầu nh&acirc;n vi&ecirc;n s&acirc;n bay chở đến ph&ograve;ng đợi nếu bị mệt hoặc do sắp đến giờ bay.</p>\r\n<p>\r\n	Ph&ograve;ng đợi c&oacute; thể chưa mở cửa, bạn c&oacute; thể ngồi ở gần đ&oacute; hoặc đi dạo xung quanh.</p>\r\n<p>\r\n	N&ecirc;n để &yacute; c&aacute;c th&ocirc;ng b&aacute;o tr&ecirc;n loa hoặc tr&ecirc;n c&aacute;c bảng điện tử.</p>\r\n<p>\r\n	Nếu bạn c&oacute; v&eacute; hạng Bussiness hay First class sẽ c&oacute; một khu vực ri&ecirc;ng d&agrave;nh cho bạn .</p>\r\n<p>\r\n	<strong><em>Ch&uacute; &yacute;:</em></strong>&nbsp;Vặn đồng hồ theo giờ địa phương để tr&aacute;nh bị động, mặc d&ugrave; bạn sẽ kh&ocirc;ng bị bỏ rơi một khi đ&atilde; v&agrave;o đến đ&acirc;y.</p>\r\n<p>\r\n	<br />\r\n	<strong>1.8. L&ecirc;n m&aacute;y bay:</strong></p>\r\n<p>\r\n	Khi đến giờ l&ecirc;n m&aacute;y bay, nh&acirc;n vi&ecirc;n của h&atilde;ng h&agrave;ng kh&ocirc;ng sẽ xuất hiện ở ph&ograve;ng đợi. H&agrave;nh kh&aacute;ch c&oacute; v&eacute; hạng cao v&agrave;o trước, tiếp đ&oacute; l&agrave; những người t&agrave;n tật, những người c&oacute; trẻ em đi c&ugrave;ng, những người c&ograve;n lại xếp h&agrave;ng lần lượt v&agrave;o hoặc nh&acirc;n vi&ecirc;n sẽ gọi theo số ghế. Bạn đưa ra Boarding Pass, nh&acirc;n vi&ecirc;n x&eacute; một phần v&agrave; đưa lại cho bạn mảnh nhỏ hơn tr&ecirc;n đ&oacute; c&oacute; ghi số ghế của bạn, bạn đi theo h&agrave;nh lang nhỏ để l&ecirc;n m&aacute;y bay hoặc ra xe bus để đến ch&acirc;n m&aacute;y bay.</p>\r\n<p>\r\n	Số ghế tr&ecirc;n m&aacute;y bay c&oacute; dạng 55B, trong đ&oacute; 55 l&agrave; h&agrave;ng ghế, chữ c&aacute;i đi k&egrave;m chỉ vị tr&iacute; ghế trong h&agrave;ng. M&aacute;y bay nhỏ thường c&oacute; 6 ghế một h&agrave;ng, trong khi m&aacute;y bay lớn c&oacute; thể c&oacute; 10 ghế hoặc hơn. Tiếp vi&ecirc;n tr&ecirc;n m&aacute;y bay sẽ hướng dẫn bạn đi đ&uacute;ng lối để đến vị tr&iacute; của bạn. Khi đến đ&uacute;ng h&agrave;ng ghế của m&igrave;nh, bạn nh&igrave;n k&yacute; hiệu chữ c&aacute;i v&agrave; h&igrave;nh vẽ tương ứng để ngồi đ&uacute;ng chỗ. H&agrave;nh l&yacute; x&aacute;ch tay để tr&ecirc;n gi&aacute; ph&iacute;a tr&ecirc;n đầu hoặc dưới ghế ngồi/ch&acirc;n của bạn. Bạn c&oacute; thể gửi tiếp vi&ecirc;n cất hộ &aacute;o comple/vest để tr&aacute;nh bị nh&agrave;u n&aacute;t.</p>\r\n<p>\r\n	Ghế ngồi c&oacute; thể ngả ra đằng sau cho tho&aacute;i m&aacute;i, ở gầm ghế ph&iacute;a trước c&oacute; thể c&oacute; c&aacute;i để ch&acirc;n. Tuy nhi&ecirc;n, l&uacute;c m&aacute;y bay cất/hạ c&aacute;nh bạn phải để lưng ghế thẳng, kh&ocirc;ng sử dụng c&aacute;i để ch&acirc;n, kh&ocirc;ng sử dụng c&aacute;c thiết bị điện tử. Mỗi ghế ngồi c&oacute; một số n&uacute;t điều khiển ri&ecirc;ng như đ&egrave;n, n&uacute;t gọi tiếp vi&ecirc;n. C&aacute;c m&aacute;y bay đường d&agrave;i c&oacute; m&agrave;n h&igrave;nh video, radio v&agrave; điều khiển cho mỗi chỗ ngồi. Lưng ghế ph&iacute;a trước c&oacute; b&agrave;n ăn gấp lại, ph&iacute;a dưới l&agrave; một tập th&ocirc;ng tin. Bạn c&oacute; thể cầm theo c&aacute;c tạp ch&iacute; quảng c&aacute;o l&uacute;c xuống m&aacute;y bay, ri&ecirc;ng hướng dẫn an to&agrave;n tr&ecirc;n m&aacute;y bay th&igrave; kh&ocirc;ng được lấy. Ph&iacute;a tr&ecirc;n ghế ngồi c&oacute; biển b&aacute;o chỉ dẫn khi n&agrave;o th&igrave; phải đeo d&acirc;y bảo hiểm, được ph&eacute;p h&uacute;t thuốc hay kh&ocirc;ng. Khi m&aacute;y bay cất/hạ c&aacute;nh hoặc khi đi v&agrave;o v&ugrave;ng xốc (c&oacute; đ&egrave;n hiệu th&ocirc;ng b&aacute;o), bạn phải đeo d&acirc;y bảo hiểm.</p>\r\n<p>\r\n	Trong khi m&aacute;y bay cất c&aacute;nh, tiếp vi&ecirc;n sẽ hướng dẫn c&aacute;c bước phải l&agrave;m khi c&oacute; sự cố.</p>\r\n<p>\r\n	Sau đ&oacute;, mỗi người được ph&aacute;t một khăn mặt để lau mặt v&agrave; tay. Đồ uống được đem ra, bạn c&oacute; thể chọn rượu mạnh (alcohol) (t&ugrave;y tuyến bay), rượu vang (red/white wine), bia, nước ngọt, nước hoa quả, nước kho&aacute;ng. Đồ ăn thường c&oacute; một m&oacute;n ch&iacute;nh (c&oacute; thịt), salad, b&aacute;nh m&igrave; tr&ograve;n nhỏ, tr&aacute;ng miệng. Thường c&oacute; 2 lựa chọn cho m&oacute;n ch&iacute;nh , v&iacute; dụ :g&agrave; nấu m&igrave; v&agrave; thịt lợn với khoai t&acirc;y, nếu đến lượt bạn m&agrave; vẫn c&ograve;n đủ hai lựa chọn th&igrave; bạn c&oacute; thể lựa m&oacute;n . Sau khi ăn xong tiếp vi&ecirc;n sẽ phục vụ caf&eacute; hoặc ch&egrave;. Đi đường d&agrave;i, chuyến bay sẽ c&oacute; 2 bữa ăn ch&iacute;nh v&agrave; một bữa phụ. Chuyến đi ngắn thường chỉ c&oacute; 1 bữa ăn, đ&ocirc;i khi kh&ocirc;ng phục vụ rượu miễn ph&iacute;. Ngo&agrave;i c&aacute;c bữa ăn bạn c&oacute; thể xin nước uống bất cứ l&uacute;c n&agrave;o. Khi ăn, bạn phải dựng ghế cho thẳng.</p>\r\n<p>\r\n	Nếu lạnh, bạn c&oacute; thể n&oacute;i tiếp vi&ecirc;n cho mượn chăn để đắp.</p>\r\n<p>\r\n	Khi đi đường d&agrave;i, thỉnh thoảng bạn n&ecirc;n ra khỏi chỗ đi lại cho đỡ mỏi v&agrave; tập c&aacute;c động t&aacute;c để thư gi&atilde;n cơ thể.</p>\r\n<p>\r\n	<strong>1.9. Xuống m&aacute;y bay:</strong>Trừ trường hợp phải xuống gấp để đi chuyến tiếp theo cho kịp thời gian, h&agrave;nh kh&aacute;ch ra khỏi m&aacute;y bay đầu ti&ecirc;n l&agrave; những người c&oacute; v&eacute; hạng cao, những người c&ograve;n lại xếp h&agrave;ng lần lượt ra. Nếu đ&ocirc;ng h&agrave;nh kh&aacute;ch hoặc m&aacute;y bay lớn, c&oacute; thể c&oacute; th&ecirc;m đường ra ở ph&iacute;a đu&ocirc;i m&aacute;y bay. C&aacute;c s&acirc;n bay hiện đại c&oacute; h&agrave;nh lang dẫn thẳng từ m&aacute;y bay v&agrave;o trong, c&aacute;c s&acirc;n bay kh&aacute;c c&oacute; thể d&ugrave;ng xe bus đưa kh&aacute;ch từ m&aacute;y bay v&agrave;o.</p>\r\n<p>\r\n	T&ugrave;y theo chuyến bay của bạn, s&acirc;n bay đ&atilde; định sẵn cổng với c&aacute;c hướng dẫn tương ứng. C&oacute; c&aacute;c trường hợp sau:</p>\r\n<p>\r\n	&bull; Bạn đi transit, điểm dừng n&agrave;y ho&agrave;n to&agrave;n độc lập với điểm đến tiếp theo (kh&ocirc;ng chung visa nhập cảnh), v&iacute; dụ đi từ H&agrave; Nội đến Amsterdam transit tại Singapore: HAN &ndash; SIN - AMS, khi đ&oacute; nếu bạn đ&atilde; c&oacute; Boarding Pass của chặng tiếp theo (Sing - Ams) bạn c&oacute; thể đến thẳng ph&ograve;ng đợi hoặc xem tr&ecirc;n bảng điện tử, nếu chưa c&oacute; Boarding Pass bạn c&oacute; thể t&igrave;m đến Transfer desk để hỏi v&agrave; l&agrave;m thủ tục check-in cho chặng tiếp theo.</p>\r\n<p>\r\n	&bull; Bạn đi transit, điểm dừng n&agrave;y v&agrave; điểm đến tiếp theo c&oacute; chung visa nhập cảnh. V&iacute; dụ, đi từ H&agrave; Nội đến New York (Mỹ) transit tại Los Angeles (Mỹ): HN &ndash; LA &ndash; NY, bạn sẽ phải l&agrave;m thủ tục nhập cảnh ở LA trước khi đi chặng tiếp theo v&igrave; chặng tiếp theo LA &ndash; NY l&agrave; tuyến nội địa.</p>\r\n<p>\r\n	&bull; Nếu đ&acirc;y l&agrave; điểm cuối của h&agrave;nh tr&igrave;nh, bạn để &yacute; c&aacute;c biển b&aacute;o chỉ hướng Arrival/Exit:</p>\r\n<p>\r\n	- Nếu tuyến bạn vừa đi l&agrave; tuyến nội địa bạn sẽ đến thẳng khu lấy đồ.</p>\r\n<p>\r\n	- C&ograve;n nếu đ&oacute; l&agrave; tuyến quốc tế bạn sẽ đến khu vực l&agrave;m thủ tục nhập cảnh.</p>\r\n<p>\r\n	<strong>1.10.&nbsp;</strong><a href=\"http://phongvegiare.vn/\" title=\"lam thu tuc nhap canh,làm thủ tục nhập cảnh\"><strong>L&agrave;m thủ tục nhập cảnh</strong></a><br />\r\n	C&oacute; thể c&oacute; nhiều quầy l&agrave;m thủ tục nhập cảnh, bạn để &yacute; biển b&aacute;o, một số quầy d&agrave;nh cho nh&acirc;n vi&ecirc;n ngoại giao/c&ocirc;ng vụ, một số d&agrave;nh cho người bản địa, số kh&aacute;c d&agrave;nh cho người nước ngo&agrave;i&hellip;</p>\r\n<p>\r\n	Tại đ&acirc;y bạn tr&igrave;nh hộ chiếu/visa, nh&acirc;n vi&ecirc;n hải quan c&oacute; thể sẽ đ&oacute;ng dấu hoặc d&aacute;n th&ecirc;m một số giấy tờ li&ecirc;n quan, t&ugrave;y theo từng nước bạn c&oacute; thể phải điền v&agrave;o một v&agrave;i mẫu đơn qui định trước (Việt Nam) hoặc phải trả lời một số c&acirc;u hỏi li&ecirc;n quan đến việc nhập cảnh mặc d&ugrave; giấy tờ của bạn hợp lệ (v&iacute; dụ ở c&aacute;c nước ph&aacute;t triển, họ sợ người từ c&aacute;c nước ngh&egrave;o sang rồi ở lại bất hợp ph&aacute;p).</p>\r\n<p>\r\n	Nếu bạn đi transit tuyến nội địa, bạn sẽ được hướng ra cổng tương ứng. Ngược lại, bạn sẽ đi thẳng ra khu lấy đồ.</p>\r\n<p>\r\n	<strong>1.11. Lấy đồ<br />\r\n	</strong>H&agrave;nh l&yacute; bạn gửi l&uacute;c check-in được đưa ra tr&ecirc;n c&aacute;c d&acirc;y chuyền. C&oacute; thể c&oacute; nhiều d&acirc;y chuyền được đ&aacute;nh số hoặc ghi chỉ dẫn, bạn xem bảng điện tử/bảng th&ocirc;ng tin để biết d&acirc;y chuyền n&agrave;o tương ứng với chuyến bay của bạn m&agrave; lấy đồ.</p>\r\n<p>\r\n	Xung quanh khu vực n&agrave;y c&oacute; c&aacute;c xe đẩy (trolley) để gi&uacute;p bạn chở đồ.</p>\r\n<p>\r\n	Nếu kh&ocirc;ng t&igrave;m thấy đồ của m&igrave;nh bạn phải th&ocirc;ng b&aacute;o ngay đến ph&ograve;ng khai b&aacute;o mất đồ/thất lạc (Lost and Found) hoặc li&ecirc;n hệ với h&atilde;ng h&agrave;ng kh&ocirc;ng của chuyến bay m&agrave; bạn vừa đi, nằm xung quanh đ&oacute; v&agrave; đưa ra cuống v&eacute; tương ứng với h&agrave;nh l&yacute; bị thất lạc. Nếu bạn đi ra khỏi khu vực n&agrave;y m&agrave; chưa khai b&aacute;o mất/thất lạc đồ th&igrave; bạn kh&ocirc;ng thể v&agrave;o lại m&agrave; khiếu nại được nữa.</p>\r\n<p>\r\n	Nếu kh&ocirc;ng c&oacute; h&agrave;nh l&yacute; gửi bạn c&oacute; thể đi thẳng ra lu&ocirc;n.</p>\r\n<p>\r\n	<strong>1.12. Đi ra</strong></p>\r\n<p>\r\n	Ở c&aacute;c nước ph&aacute;t triển hoặc đề cao t&iacute;nh tự gi&aacute;c, c&oacute; hai loại cửa để đi ra. Nếu bạn mang theo c&aacute;c mặt h&agrave;ng thuộc danh mục đ&aacute;nh thuế bạn phải đi ra cửa tương ứng để khai b&aacute;o v&agrave; đ&oacute;ng thuế, ngược lại nếu kh&ocirc;ng c&oacute; c&aacute;c mặt h&agrave;ng phải đ&oacute;ng thuế bạn đi ra cửa Nothing to declare.</p>\r\n<p>\r\n	S&acirc;n bay Nội B&agrave;i (H&agrave; Nội) kh&ocirc;ng chia cửa, bạn phải đưa đồ l&ecirc;n m&aacute;y soi v&agrave; khai b&aacute;o với nh&acirc;n vi&ecirc;n thuế ở đ&acirc;y.</p>\r\n<p>\r\n	Để biết mặt h&agrave;ng n&agrave;o thuộc danh mục phải đ&oacute;ng thuế, bạn c&oacute; thể t&igrave;m hiểu trước qua internet, qua đại sứ qu&aacute;n&hellip; hoặc hỏi tại s&acirc;n bay. C&aacute;c mặt h&agrave;ng như thuốc l&aacute;, rượu mạnh, nước hoa. Lưu &yacute;, mỗi h&agrave;nh kh&aacute;ch chỉ được mang một lượng nhất định, mang qu&aacute; sẽ phải đ&oacute;ng thuế.</p>\r\n<p>\r\n	Ra khỏi cửa khai b&aacute;o thuế l&agrave; bạn đ&atilde; đến khu vực chung của nơi đến (Arrival), bạn c&oacute; thể thu&ecirc; xe hoặc ra c&aacute;c phương tiện giao th&ocirc;ng c&ocirc;ng cộng để đi tiếp.</p>\r\n<p>\r\n	Nếu c&oacute; người ra đ&oacute;n, tốt nhất l&agrave; bạn n&ecirc;n hẹn v&agrave; đứng đợi ngay khi ra khỏi cửa khai b&aacute;o thuế.</p>\r\n<p>\r\n	Ch&uacute; &yacute; đồ đạc kẻo mất.<br />\r\n	<strong>1.13. Transit:</strong></p>\r\n<p>\r\n	Nếu đ&atilde; c&oacute; Boarding Pass cho chặng tiếp theo, bạn c&oacute; thể đi thẳng đến ph&ograve;ng đợi ghi tr&ecirc;n thẻ (nếu c&oacute;) hoặc xem tr&ecirc;n bảng điện tử.</p>\r\n<p>\r\n	Nếu chưa c&oacute; Boarding Pass, bạn c&oacute; thể t&igrave;m đến c&aacute;c điểm c&oacute; biển Transfer Desk để hỏi v&agrave; l&agrave;m thủ tục check-in cho chặng tiếp theo.</p>\r\n<p>\r\n	Trong trường hợp h&agrave;nh l&yacute; gửi của bạn kh&ocirc;ng được đưa thẳng đến điểm cuối, bạn phải ra lấy đồ v&agrave; l&agrave;m lại c&aacute;c thủ tục như ban đầu.</p>\r\n<p>\r\n	Nếu mọi chuyện tốt đẹp, bạn đang lặp lại c&aacute;c bước, v&agrave; hiện đang ở bước &ldquo;V&agrave;o ph&ograve;ng đợi&rdquo;.</p>\r\n<p>\r\n	Nhớ chỉnh lại đồng hồ theo giờ địa phương.</p>\r\n<p>\r\n	Nếu c&ograve;n nhiều thời gian bạn c&oacute; thể đi dạo trong khu vực n&agrave;y, một số s&acirc;n bay c&oacute; c&aacute;c dịch vụ gi&uacute;p kh&aacute;ch giải tr&iacute; v&iacute; dụ như s&acirc;n bay Singapore c&oacute; c&aacute;c tour v&agrave;o th&agrave;nh phố miễn ph&iacute;, c&aacute;c show game&hellip;</p>', '#', '0');
