INSERT INTO `intro` VALUES ('9', 'MEXICO', 'images/pic/gal01.jpg', 'MEXICO', 'Nổi tiếng với thành phố cổ Teotihuacan gồm khu đền thờ Quetzalcoatl, Kim tự tháp của Mặt Trời, Kim tự tháp của Mặt trăng, Đại lộ của người chết. Hai món ăn nổi tiếng là món gỏi cây xương rồng và Tacos, rượu cất từ cây xương rồng và rượu Tequila.', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
