INSERT INTO `intro` VALUES ('11', 'ANH QUỐC', 'images/pic/gal01.jpg', 'ANH QUỐC', 'Nổi tiếng với  bức tường Hadrian, tháp BigBen, Và những tòa lâu đài, những CLB bóng đá nổi tiếng, vương quốc của sương mù.', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('12', 'Pháp', 'images/pic/gal01.jpg', 'Pháp', 'Nổi tiếng với các bảo tàng, Bordeaux - xứ sở của rượu vang', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('13', 'Ai cập', 'images/pic/gal01.jpg', 'Ai cập', 'Nổi tiếng với sa mạc Sahara và kim tử tháp, xác ướp và những lời nguyền, sông nile dài nhất thế giới.', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('14', 'Thụy Sĩ', 'images/pic/gal01.jpg', 'Thụy Sĩ', 'Nổi tiếng về ngành công nghiệp sản xuất đồng hồ, hệ thống ngân hàng uy tín và nổi tiếng nhất toàn cầu, tham gia trượt tuyết và các thế vận hội mùa đông, ngắm các lâu đài cổ kính, thưởng thức các món socola tuyệt hảo', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('15', 'Ý', 'images/pic/gal01.jpg', 'Ý', 'Nổi tiếng với tháp nghiêng Pisa, món mì, bánh Piza nền bóng đá, là cái nôi văn hóa phương tây, đấu trường, tòa thánh tự trị vatican', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('16', 'Bỉ', 'images/pic/gal01.jpg', 'Bỉ', 'Địa danh Durbuy ( điểm du lịch nông thôn nổi bật nhất châu Âu) Thành phố Ath lãng mạn bậc nhất Châu Âu, Công viên thiên nhiên Viroinval.', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('17', 'Đức', 'images/pic/gal01.jpg', 'Đức', 'Nổi tiếng với lễ hội bia, lễ hội Love Parade. Ngôi làng cổ ở Wangen, lễ hội pháo hoa ở Frankfurt, đất nước có 350 nhà hát lớn, 120 nhà hát Opera và nơi sinh ra rất nhiều nhà soạn nhạc của thế giới như: S. Bach, Beethoven, Johannes Brahms…', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('18', 'Thổ Nhĩ Kỳ', 'images/pic/gal01.jpg', 'Thổ Nhĩ Kỳ', 'Nổi tiếng với thành phố ngầm, kinh khí cầu, thành phố Istanbul (ngã tư của văn minh Đông Tây), các tu viện, Thành phố cổ đại Ephesus , Đền thờ Blue Mosque, Nhà thờ Hagia Sophia', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('19', 'Áo', 'images/pic/gal01.jpg', 'Áo', 'Nổi tiếng với Swarowski Crystal Worlds viện bảo tàng pha lê trong lòng đất. Nơi sinh của Mozart, Salzburg.Cung điện hoàng gia Schönbrunn, Vienna', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('20', 'Canada', 'images/pic/gal01.jpg', 'Canada', 'Núi đá Rockies, Thác nước Niagara, Công viên quốc gia Pacific Rim, Đường mòn Cabot, Đảo Baffin, Thành phố Vancouver và Victoria, Thành phố cổ Québec, Vịnh Fundy', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('21', 'Israel', 'images/pic/gal01.jpg', 'Israel', 'Thành phố cổ Acre (Akko), Biển Chết', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');
INSERT INTO `intro` VALUES ('22', 'Lào', 'images/pic/gal01.jpg', 'Lào', 'Cánh đồng chum ở Xiêng Khoảng, thành phố cổ Luang Prabang (được  UNESCO là một di sản thế giới), hang động Pak Ou', 'images/pic/gal01.jpg', 'images/pic/gal02.jpg', 'images/pic/gal03.jpg', '0');

